import React, { useState } from "react";
import { addEvent } from "../api";
import { useNavigate } from "react-router-dom";

export default function AddEvent({ user, onBack }) {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    description: "",
    start_datetime: "",
    end_datetime: "",
    issue_date: "",
    location: "",
    level: "jila",
    event_type: "meeting",
  });
  const [photos, setPhotos] = useState([]);
  const [photoPreviews, setPhotoPreviews] = useState([]);
  const [media, setMedia] = useState([]);
  const [mediaPreviews, setMediaPreviews] = useState([]);
  const [video, setVideo] = useState(null);
  const [videoError, setVideoError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  function handleFiles(e, setter, previewSetter, maxFiles = 10) {
    const fl = Array.from(e.target.files || []);
    if (fl.length > maxFiles) {
      alert(`अधिकतम ${maxFiles} फ़ाइलें अनुमति हैं`);
      return;
    }
    setter(fl);
    // create previews for images only
    const imgs = fl.filter((f) => f.type.startsWith("image/"));
    Promise.all(imgs.map((f) => fileToDataUrl(f))).then(previews => previewSetter(previews));
  }

  function fileToDataUrl(file) {
    return new Promise((res) => {
      const fr = new FileReader();
      fr.onload = () => res(fr.result);
      fr.onerror = () => res(null);
      fr.readAsDataURL(file);
    });
  }

  function handleVideoChange(e) {
    setVideoError(null);
    const f = e.target.files[0];
    if (!f) {
      setVideo(null);
      return;
    }
    // enforce minimum 10MB
    if (f.size < 10 * 1024 * 1024) {
      setVideoError("वीडियो कम से कम 10MB होना चाहिए");
      setVideo(null);
      return;
    }
    setVideo(f);
  }

  async function submit(e) {
    e.preventDefault();
    if (!user || !user.id) { alert("कृपया पहले लॉगिन करें"); return; }
    setSubmitting(true);
    try {
      const fd = new FormData();
      Object.keys(form).forEach(k => fd.append(k, form[k]));
      fd.append("created_by", user.id);
      photos.forEach(p => fd.append("photos", p));
      media.forEach(m => fd.append("media_photos", m));
      if (video) fd.append("video", video);

      const r = await addEvent(fd);
      if (r.error) alert("त्रुटि: " + r.error);
      else {
        alert("इवेंट सफलतापूर्वक जोड़ा गया");
        onBack ? onBack() : navigate("/");
      }
    } catch (err) {
      console.error(err);
      alert("सबमिट में त्रुटि");
    } finally {
      setSubmitting(false);
    }
  }

  return (
    <div className="canvas">
      <button className="button ghost" onClick={() => onBack ? onBack() : navigate(-1)}>वापस</button>
      <h3>नया इवेंट जोड़ें</h3>
      <form onSubmit={submit}>
        <div style={{ marginTop: 8 }}>
          <label>नाम</label>
          <input value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} />
        </div>

        <div style={{ marginTop: 8 }}>
          <label>विवरण</label>
          <textarea value={form.description} onChange={(e) => setForm({ ...form, description: e.target.value })} />
        </div>

        <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
          <div style={{ flex: 1 }}>
            <label>शुरू (YYYY-MM-DD HH:MM:SS)</label>
            <input value={form.start_datetime} onChange={(e) => setForm({ ...form, start_datetime: e.target.value })} placeholder="YYYY-MM-DD HH:MM:SS" />
          </div>
          <div style={{ flex: 1 }}>
            <label>समाप्त (YYYY-MM-DD HH:MM:SS)</label>
            <input value={form.end_datetime} onChange={(e) => setForm({ ...form, end_datetime: e.target.value })} placeholder="YYYY-MM-DD HH:MM:SS" />
          </div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>Issue Date (YYYY-MM-DD)</label>
          <input value={form.issue_date} onChange={(e) => setForm({ ...form, issue_date: e.target.value })} placeholder="YYYY-MM-DD" />
        </div>

        <div style={{ marginTop: 8 }}>
          <label>स्थान</label>
          <input value={form.location} onChange={(e) => setForm({ ...form, location: e.target.value })} />
        </div>

        <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
          <div style={{ flex: 1 }}>
            <label>स्तर</label>
            <select value={form.level} onChange={(e) => setForm({ ...form, level: e.target.value })}>
              <option value="jila">jila</option>
              <option value="block">block</option>
            </select>
          </div>
          <div style={{ flex: 1 }}>
            <label>इवेंट प्रकार</label>
            <select value={form.event_type} onChange={(e) => setForm({ ...form, event_type: e.target.value })}>
              <option value="Dhrana">Dhrana</option>
              <option value="meeting">meeting</option>
              <option value="band">band</option>
              <option value="raily">raily</option>
              <option value="sabha">sabha</option>
              <option value="gayapan">gayapan</option>
            </select>
          </div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>Photos (max 10)</label>
          <input type="file" accept="image/*" multiple onChange={(e) => handleFiles(e, setPhotos, setPhotoPreviews, 10)} />
          <div style={{ display: "flex", gap: 8, marginTop: 8 }}>{photoPreviews.map((p, i) => <img key={i} src={p} alt={`p${i}`} style={{ height: 80, borderRadius: 8 }} />)}</div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>Media Coverage Photos (max 5)</label>
          <input type="file" accept="image/*" multiple onChange={(e) => handleFiles(e, setMedia, setMediaPreviews, 5)} />
          <div style={{ display: "flex", gap: 8, marginTop: 8 }}>{mediaPreviews.map((p, i) => <img key={i} src={p} alt={`m${i}`} style={{ height: 80, borderRadius: 8 }} />)}</div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>Video (min 10MB)</label>
          <input type="file" accept="video/*" onChange={handleVideoChange} />
          {videoError && <div style={{ color: "red" }}>{videoError}</div>}
        </div>

        <div style={{ marginTop: 12, display: "flex", gap: 8 }}>
          <button className="button" type="submit" disabled={submitting}>{submitting ? "जोड़ रहे हैं..." : "जोड़ें"}</button>
          <button className="button ghost" type="button" onClick={() => onBack ? onBack() : navigate(-1)}>रद्द करें</button>
        </div>
      </form>
    </div>
  );
}
