import React, { useState, useEffect } from 'react';
import { listEvents } from '../api';

/*
  Props expected:
    - onOpen(id)
    - onEdit(id)
    - onAdd()
    - onReport(id)
    - onLogout()
    - user
*/

export default function Home({ onOpen, onEdit, onAdd, onReport, onLogout, user }) {
  const [type, setType] = useState('ongoing'); // 'ongoing' | 'previous'
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchEvents(type);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [type]);

  async function fetchEvents(t) {
    setLoading(true);
    try {
      const res = await listEvents(t);
      setEvents(Array.isArray(res) ? res : []);
    } catch (err) {
      console.error('Failed to load events', err);
      setEvents([]);
    } finally {
      setLoading(false);
    }
  }

  function formatDateRange(e) {
    const s = e?.start_datetime || '';
    const ed = e?.end_datetime || '';
    if (!s && !ed) return '-';
    return `${s}${s && ed ? ' â€” ' : ''}${ed}`;
  }

  const filtered = events.filter(ev => {
    if (!search) return true;
    const s = search.trim().toLowerCase();
    return (
      (ev.name || '').toLowerCase().includes(s) ||
      (ev.description || '').toLowerCase().includes(s) ||
      (ev.location || '').toLowerCase().includes(s)
    );
  });

  return (
    <div className="canvas" style={{ minHeight: 'calc(100vh - 120px)' }}>
      {/* Header row */}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', marginBottom: 14 }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 12 }}>
          <SiteLogo />
          <div>
            <div style={{ fontSize: 18, fontWeight: 700 }}>INC Event Management</div>
            <div style={{ fontSize: 13, color: '#555' }}>{user ? `Welcome, ${user.name || user.id}` : 'Please login to add or edit events'}</div>
          </div>
        </div>

        <div style={{ display: 'flex', gap: 8 }}>
          {user && user.role === 'admin' && <button className="button" onClick={onAdd}>+ Add Event</button>}
          <button className="button ghost" onClick={() => fetchEvents(type)}>Refresh</button>
          {user ? <button className="button secondary" onClick={onLogout}>Logout</button> : <a className="button" href="/login">Login</a>}
        </div>
      </div>

      {/* Filters */}
      <div style={{ display: 'flex', alignItems: 'center', gap: 12, marginBottom: 12 }}>
        <label style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
          <input type="radio" name="evType" checked={type === 'ongoing'} onChange={() => setType('ongoing')} /> Ongoing
        </label>
        <label style={{ display: 'flex', gap: 8, alignItems: 'center' }}>
          <input type="radio" name="evType" checked={type === 'previous'} onChange={() => setType('previous')} /> Previous
        </label>

        <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
          <input
            type="search"
            placeholder="Search name, location, description..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ padding: 8, borderRadius: 8, border: '1px solid rgba(0,0,0,0.08)', minWidth: 260 }}
          />
          <button className="button ghost" onClick={() => setSearch('')}>Clear</button>
        </div>
      </div>

      {/* Table */}
      <div className="table-wrap">
        {loading ? (
          <div className="empty">Loading...</div>
        ) : filtered.length === 0 ? (
          <div className="empty">
            <div style={{ fontSize: 18, fontWeight: 700 }}>No events found</div>
            <div style={{ color: '#666' }}>Try clearing filters or switch type</div>
          </div>
        ) : (
          <table className="table" role="table" aria-label="Events table">
            <thead>
              <tr>
                <th style={{ width: 70 }}>S. No.</th>
                <th>Event Details (Name)</th>
                <th style={{ width: 240 }}>Event Date</th>
                <th style={{ width: 260 }}>Action</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((e, idx) => (
                <tr key={e.id || idx}>
                  <td>{idx + 1}</td>
                  <td>
                    <a href="#" onClick={(ev) => { ev.preventDefault(); onOpen && onOpen(e.id); }}>{e.name}</a>
                  </td>
                  <td>{formatDateRange(e)}</td>
                  <td>
                    <div style={{ display: 'flex', gap: 8 }}>
                      <button className="button small" onClick={() => onOpen && onOpen(e.id)}>Show Details</button>

                      <button
                        className="button secondary small"
                        onClick={() => onEdit && onEdit(e.id)}
                        disabled={type === 'previous'}
                        title={type === 'previous' ? 'Previous events cannot be updated' : 'Update event'}
                      >
                        Update
                      </button>

                      {user && user.role === 'admin' && <button className="button small" onClick={() => onReport && onReport(e.id)}>Report</button>}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

/* Small SiteLogo */
function SiteLogo(){
  const logoUrl = '/logo192.png';
  const [ok,setOk] = useState(true);
  return ok ? (
    <img src={logoUrl} alt="logo" onError={() => setOk(false)} style={{ width:64, height:64, borderRadius:12, objectFit:'cover', boxShadow:'0 8px 20px rgba(0,0,0,0.06)' }} />
  ) : (
    <div className="logo-badge" style={{ width:64, height:64, borderRadius:12, fontSize:18 }}>INC</div>
  );
}
