// src/App.js
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
  useParams,
} from "react-router-dom";

import Home from "./components/Home";
import Login from "./components/Login";
import AddEvent from "./components/AddEvent";
import UpdateEvent from "./components/UpdateEvent";
import EventDetails from "./components/EventDetails";
import { listEvents } from "./api";
import "./styles.css";

/* Small Recent dropdown (kept optional) */
function NavRecent({ user }) {
  const [recent, setRecent] = useState([]);
  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const r = await listEvents("ongoing");
        if (!mounted) return;
        setRecent(Array.isArray(r) ? r.slice(0, 6) : []);
      } catch (err) {
        console.error(err);
      }
    })();
    return () => (mounted = false);
  }, []);
  return (
    <details style={{ position: "relative" }}>
      <summary className="button ghost" style={{ listStyle: "none", cursor: "pointer" }}>
        हाल के
      </summary>
      <div
        style={{
          position: "absolute",
          right: 0,
          marginTop: 8,
          background: "white",
          boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
          borderRadius: 8,
          padding: 12,
          minWidth: 220,
          zIndex: 60,
        }}
      >
        {recent.length === 0 ? (
          <div className="muted">कोई हाल के इवेंट नहीं</div>
        ) : (
          recent.map((ev) => (
            <div key={ev.id} style={{ marginBottom: 8 }}>
              <div style={{ fontWeight: 700, marginBottom: 4 }}>
                <Link to={`/event/${ev.id}`}>{ev.name}</Link>
              </div>
              <div style={{ display: "flex", gap: 8 }}>
                <Link className="small" to={`/event/${ev.id}`} style={{ color: "#0f62fe" }}>
                  विवरण
                </Link>
                {user && user.role === "admin" ? (
                  <Link className="small" to={`/update/${ev.id}`} style={{ color: "#475569" }}>
                    अपडेट
                  </Link>
                ) : (
                  <span className="small muted">अपडेट (admin)</span>
                )}
              </div>
            </div>
          ))
        )}
      </div>
    </details>
  );
}

function AppShell() {
  // user persisted in localStorage (if used elsewhere)
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("inc_user") || "null");
    } catch {
      return null;
    }
  });

  const navigate = useNavigate();

  useEffect(() => {
    if (user) localStorage.setItem("inc_user", JSON.stringify(user));
    else localStorage.removeItem("inc_user");
  }, [user]);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("inc_user");
    navigate("/");
  };

  // event id controller in navbar
  const [navEventId, setNavEventId] = useState("");

  // Wrappers for route components that need hooks and props
  function HomeWrapper() {
    return (
      <Home
        user={user}
        onAdd={() => navigate("/add")}
        onEdit={(id) => navigate(`/update/${id}`)}
        onOpen={(id) => navigate(`/event/${id}`)}
        onReport={(id) => navigate(`/event/${id}/report`)}
        onLogout={handleLogout}
      />
    );
  }
  function LoginWrapper() {
    const onLogin = (u) => {
      setUser(u);
      navigate("/");
    };
    return <Login onLogin={onLogin} />;
  }
  function AddWrapper() {
    const onBack = () => navigate(-1);
    return <AddEvent user={user} onBack={onBack} />;
  }
  function UpdateWrapper() {
    const { id } = useParams();
    const onBack = () => navigate(-1);
    return <UpdateEvent id={id} user={user} onBack={onBack} />;
  }
  function EventWrapper() {
    const { id } = useParams();
    const onBack = () => navigate(-1);
    const onEdit = (eid) => navigate(`/update/${eid}`);
    return <EventDetails id={id} user={user} onBack={onBack} onEdit={onEdit} />;
  }

  // Nav actions
  const goToShow = () => {
    const id = String(navEventId || "").trim();
    if (!id) { alert("कृपया Event ID डालें"); return; }
    navigate(`/event/${id}`);
    setNavEventId("");
  };
  const goToUpdate = () => {
    const id = String(navEventId || "").trim();
    if (!id) { alert("कृपया Event ID डालें"); return; }
    navigate(`/update/${id}`);
    setNavEventId("");
  };

  return (
    <>
      <header className="header">
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <Link to="/">
            <img src="/logo192.png" alt="logo" style={{ width: 44, height: 44, borderRadius: 8 }} onError={(e) => (e.target.style.display = "none")} />
          </Link>
          <h1>INC Event Management</h1>
        </div>

        <nav style={{ marginLeft: "auto", display: "flex", gap: 10, alignItems: "center" }}>
          <Link className="button" to="/">Home</Link>

          {/* Event ID input + Show / Update */}
          <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
            <input
              placeholder="Event ID डालें"
              value={navEventId}
              onChange={(e) => setNavEventId(e.target.value)}
              style={{ padding: 8, borderRadius: 8, border: "1px solid rgba(0,0,0,0.08)", width: 140 }}
            />
            <button className="button small" onClick={goToShow}>Show Details</button>
            <button className="button secondary small" onClick={goToUpdate}>Update Page</button>
          </div>

          <Link className="button" to="/login">Login</Link>

          {/* Optional recent events dropdown */}
          <NavRecent user={user} />

          {/* Add Event - only shown to admin */}
          {user && user.role === "admin" && <Link className="button" to="/add">Add Event</Link>}

          {user ? (
            <button className="button ghost" onClick={handleLogout}>Logout</button>
          ) : null}
        </nav>
      </header>

      <main>
        <div style={{ maxWidth: 1200, margin: "20px auto", padding: 12 }}>
          <Routes>
            <Route path="/" element={<HomeWrapper />} />
            <Route path="/login" element={<LoginWrapper />} />
            <Route path="/add" element={<AddWrapper />} />
            <Route path="/update/:id" element={<UpdateWrapper />} />
            <Route path="/event/:id" element={<EventWrapper />} />
          </Routes>
        </div>
      </main>
    </>
  );
}

export default function App() {
  return (
    <Router>
      <AppShell />
    </Router>
  );
}
