import React, { useState, useEffect, useMemo } from 'react';
import { listEvents } from '../api';

/*
  Props expected:
    - onOpen(id)
    - user
    - onAdd()
    - onEdit(id)
    - onReport(id)
    - onLogout()
*/

export default function Home({ onOpen, user, onAdd, onEdit, onReport, onLogout }) {
  const [type, setType] = useState('ongoing');
  const [events, setEvents] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);

  // pagination
  const [page, setPage] = useState(1);
  const PAGE_SIZE = 8;

  // stats (optional)
  const [countOngoing, setCountOngoing] = useState(null);
  const [countPrevious, setCountPrevious] = useState(null);

  useEffect(() => {
    fetchEvents(type);
    if (countOngoing === null || countPrevious === null) fetchCounts();
    setPage(1);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [type]);

  async function fetchEvents(t) {
    setLoading(true);
    try {
      const r = await listEvents(t);
      setEvents(Array.isArray(r) ? r : []);
    } catch (err) {
      console.error('Error loading events:', err);
      setEvents([]);
    } finally {
      setLoading(false);
    }
  }

  async function fetchCounts() {
    try {
      const o = await listEvents('ongoing');
      const p = await listEvents('previous');
      setCountOngoing(Array.isArray(o) ? o.length : 0);
      setCountPrevious(Array.isArray(p) ? p.length : 0);
    } catch (err) {
      console.error('failed to fetch counts', err);
      setCountOngoing(0);
      setCountPrevious(0);
    }
  }

  const filtered = useMemo(() => {
    if (!search) return events;
    const s = search.trim().toLowerCase();
    return events.filter(ev => {
      return (ev.name || '').toLowerCase().includes(s) ||
             (ev.description || '').toLowerCase().includes(s) ||
             (ev.location || '').toLowerCase().includes(s);
    });
  }, [events, search]);

  const totalPages = Math.max(1, Math.ceil(filtered.length / PAGE_SIZE));
  const paged = filtered.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  // If user not logged in, show a larger hero / welcome area that fills viewport
  if (!user) {
    return (
      <main style={{ padding: 0 }}>
        <div className="canvas" style={{ minHeight: 'calc(100vh - 120px)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ maxWidth: 980, width: '100%', padding: 28 }}>
            <div style={{ display: 'flex', gap: 20, alignItems: 'center', flexWrap: 'wrap' }}>
              <div style={{ flex: '0 0 140px' }}>
                <div className="logo-badge" style={{ width: 140, height: 140, borderRadius: 18, fontSize: 36 }}>INC</div>
              </div>
              <div style={{ flex: 1, minWidth: 260 }}>
                <h1 style={{ margin: 0, fontSize: 34, lineHeight: 1.05 }}>इवेंट प्रबंधन</h1>
                <p style={{ marginTop: 12, marginBottom: 18, fontSize: 17, color: '#495057' }}>
                  यहाँ आप सभी इवेंट देख सकते हैं। लॉगिन करें ताकि आप नया इवेंट जोड़ सकें या मौजूदा इवेंट अपडेट कर सकें।
                </p>

                <div style={{ display: 'flex', gap: 12, flexWrap: 'wrap' }}>
                  <button className="button" onClick={() => (window.location.href = '/login')}>लॉगिन करें</button>
                  <button className="button ghost" onClick={() => { /* show events read-only */ setType('ongoing'); fetchEvents('ongoing'); }}>चल रहे इवेंट देखें</button>
                </div>
              </div>
              <div style={{ flex: '0 0 260px', minWidth: 200 }}>
                <div style={{ background: 'linear-gradient(180deg,#fff,#fbfdff)', borderRadius: 12, padding: 14, boxShadow: 'var(--shadow-sm)' }}>
                  <div style={{ fontSize: 13, color: 'var(--muted)' }}>त्वरित तथ्य</div>
                  <div style={{ marginTop: 8, display: 'flex', gap: 12 }}>
                    <div style={{ flex: 1 }}>
                      <div style={{ fontSize: 22, fontWeight: 700 }}>{countOngoing === null ? '—' : countOngoing}</div>
                      <div className="small muted">चल रहे</div>
                    </div>
                    <div style={{ flex: 1 }}>
                      <div style={{ fontSize: 22, fontWeight: 700 }}>{countPrevious === null ? '—' : countPrevious}</div>
                      <div className="small muted">पिछले</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div style={{ marginTop: 20 }}>
              {/* quick sample list (first 4 events) */}
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit,minmax(220px,1fr))', gap: 12 }}>
                {events.slice(0, 4).map((e, i) => (
                  <div key={e.id || i} style={{ padding: 12, borderRadius: 10, background: '#fff', boxShadow: 'var(--shadow-sm)' }}>
                    <div style={{ fontWeight: 700 }}>{e.name}</div>
                    <div className="muted small" style={{ marginTop: 6 }}>{formatDateRange(e)}</div>
                    <div style={{ marginTop: 8, display: 'flex', justifyContent: 'space-between' }}>
                      <button className="button small" onClick={() => onOpen(e.id)}>देखें</button>
                      <button className="button ghost small" onClick={() => onOpen(e.id)}>विस्तार</button>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </main>
    );
  }

  // Logged-in full view
  return (
    <div className="canvas" style={{ minHeight: 'calc(100vh - 120px)' }}>
      <div className="hero" style={{ marginBottom: 18 }}>
        <div className="hero-left">
          <div className="logo-badge">INC</div>
          <div>
            <div className="hero-title">इंडियन नेशनल कांग्रेस — इवेंट्स</div>
            <div className="hero-sub">स्वागत, <strong>{user.name || user.id}</strong> — यहाँ आपके इवेंट्स हैं</div>
          </div>
        </div>

        <div style={{ display: 'flex', gap: 10, alignItems: 'center' }}>
          {user && user.role === 'admin' && <button className="button" onClick={onAdd}>+ नया इवेंट</button>}
          <button className="button ghost" onClick={() => { setSearch(''); setType('ongoing'); fetchEvents('ongoing'); }}>रिफ्रेश</button>
          <button className="button secondary" onClick={onLogout}>लॉग आउट</button>
        </div>
      </div>

      <div className="stats" style={{ marginBottom: 18 }}>
        <div className="stat">
          <div style={{ flex: 1 }}>
            <div className="num">{countOngoing === null ? '—' : countOngoing}</div>
            <div className="label">चल रहे इवेंट</div>
          </div>
          <div style={{ alignSelf: 'center' }}>📅</div>
        </div>

        <div className="stat">
          <div style={{ flex: 1 }}>
            <div className="num">{countPrevious === null ? '—' : countPrevious}</div>
            <div className="label">पिछले इवेंट</div>
          </div>
          <div style={{ alignSelf: 'center' }}>🎞️</div>
        </div>

        <div className="stat">
          <div style={{ flex: 1 }}>
            <div className="num">{events.length}</div>
            <div className="label">दिखाए जा रहे</div>
          </div>
          <div style={{ alignSelf: 'center' }}>📋</div>
        </div>
      </div>

      <div className="controls-row" style={{ marginBottom: 14 }}>
        <div className="search">
          <input type="search" placeholder="खोजें नाम, स्थान या विवरण..." value={search} onChange={(e) => setSearch(e.target.value)} />
          <div style={{ display: 'flex', gap: 8 }}>
            <button className={`button ${search ? '' : 'secondary'}`} onClick={() => setSearch('')}>साफ़ करें</button>
          </div>
        </div>

        <div className="filter-controls">
          <label style={{ display: 'flex', gap: 6, alignItems: 'center', fontWeight: 700 }}>
            <input type="radio" name="etype" checked={type === 'ongoing'} onChange={() => setType('ongoing')} /> चल रही
          </label>
          <label style={{ display: 'flex', gap: 6, alignItems: 'center', fontWeight: 700 }}>
            <input type="radio" name="etype" checked={type === 'previous'} onChange={() => setType('previous')} /> पिछले
          </label>
        </div>
      </div>

      {loading ? (
        <div className="empty">लोड हो रहा है…</div>
      ) : filtered.length === 0 ? (
        <div className="empty">
          <div style={{ fontSize: 18, fontWeight: 700 }}>कोई इवेंट नहीं मिला</div>
          <div className="muted">फ़िल्टर साफ़ करें या नया इवेंट जोड़ें</div>
        </div>
      ) : (
        <>
          <div className="events-grid" style={{ marginTop: 12 }}>
            {paged.map((e, i) => {
              let thumb = null;
              try {
                const photos = Array.isArray(e.photos) ? e.photos : (typeof e.photos === 'string' ? JSON.parse(e.photos || '[]') : []);
                if (photos && photos.length) thumb = photos[0];
              } catch (err) { /* ignore parse errors */ }

              return (
                <div className="event-card" key={e.id || i}>
                  <div className="event-top">
                    <div className="thumb">
                      {thumb ? <img src={thumb} alt={e.name} /> : <div style={{display:'flex',alignItems:'center',justifyContent:'center',height:'100%',color:'var(--muted)'}}>No Image</div>}
                    </div>
                    <div className="event-meta">
                      <div className="event-title">{e.name}</div>
                      <div className="event-sub">{formatDateRange(e)} • {e.location}</div>
                      <div style={{ marginTop: 8 }} className="badges">
                        <div className="badge">{e.level || 'स्तर N/A'}</div>
                        <div className="badge">{e.event_type || 'प्रकार N/A'}</div>
                      </div>
                    </div>
                  </div>

                  <div style={{ color: 'var(--muted)', fontSize: 13, minHeight: 42 }}>
                    {e.description ? (e.description.length > 120 ? e.description.slice(0, 120) + '…' : e.description) : <span className="muted">विवरण उपलब्ध नहीं</span>}
                  </div>

                  <div className="card-actions">
                    <button className="button" onClick={() => onOpen(e.id)}>डिटेल्स</button>
                    <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
                      <button className="button secondary" onClick={() => onEdit(e.id)}>अपडेट</button>
                      {user && user.role === 'admin' && <button className="button" onClick={() => onReport(e.id)}>रिपोर्ट</button>}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          <div className="pagination" style={{ marginTop: 18 }}>
            <button className="page-btn" onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1}>Prev</button>
            {Array.from({ length: totalPages }).map((_, idx) => {
              const p = idx + 1;
              return (
                <button key={p} className={`page-btn ${p === page ? 'active' : ''}`} onClick={() => setPage(p)}>{p}</button>
              );
            })}
            <button className="page-btn" onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages}>Next</button>
          </div>
        </>
      )}

      <div className="table-wrap" style={{ marginTop: 18 }}>
        <table className="table" aria-label="Event table">
          <thead>
            <tr>
              <th>#</th>
              <th>नाम</th>
              <th>तिथि</th>
              <th>स्थान</th>
              <th>कार्य</th>
            </tr>
          </thead>
          <tbody>
            {filtered.slice(0, 12).map((e, i) => (
              <tr key={e.id || i}>
                <td>{i + 1}</td>
                <td><a href="#" onClick={(ev) => { ev.preventDefault(); onOpen(e.id); }}>{e.name}</a></td>
                <td>{formatDateRange(e)}</td>
                <td>{e.location}</td>
                <td>
                  <div style={{ display: 'flex', gap: 8 }}>
                    <button className="button small" onClick={() => onOpen(e.id)}>देखें</button>
                    <button className="button secondary small" onClick={() => onEdit(e.id)}>अपडेट</button>
                    {user && user.role === 'admin' && <button className="button small" onClick={() => onReport(e.id)}>रिपोर्ट</button>}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  // helper
  function formatDateRange(ev) {
    if (!ev) return '';
    const s = ev.start_datetime || '';
    const e = ev.end_datetime || '';
    if (!s && !e) return '-';
    return `${s}${s && e ? ' — ' : ''}${e}`;
  }
}
