// src/components/EventDetails.js
import React, { useEffect, useState } from "react";
import { getEvent, markViewed } from "../api";

/* local storage keys for monthly count */
function keyMonthViews(userId, monthKey) {
  return `inc_user_${userId}_views_${monthKey}`;
}

export default function EventDetails({ id, user, onBack, onEdit }) {
  const [event, setEvent] = useState(null);
  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, [id]);

  async function load() {
    const e = await getEvent(id);
    setEvent(e || null);
    // mark viewed (backend) and update client monthly counter
    try {
      if (user && user.id) {
        await markViewed(id, user.id);
        // increment local storage month counter
        const d = new Date();
        const monthKey = `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
        const k = keyMonthViews(user.id, monthKey);
        const cur = parseInt(localStorage.getItem(k) || "0", 10) || 0;
        localStorage.setItem(k, String(cur + 1));
      }
    } catch (err) {
      console.error("markViewed failed", err);
    }
  }

  if (!event) return <div className="canvas">लोड हो रहा है...</div>;

  function safeParse(val) {
    if (!val) return [];
    if (Array.isArray(val)) return val;
    try {
      return JSON.parse(val || "[]");
    } catch {
      return [];
    }
  }

  const photos = safeParse(event.photos);
  const media_photos = safeParse(event.media_photos);

  return (
    <div className="canvas">
      <button className="button ghost" onClick={onBack}>वापस</button>
      <h2 style={{ marginTop: 12 }}>{event.name}</h2>
      <div style={{ marginTop: 8 }}><strong>विवरण:</strong> {event.description}</div>
      <div style={{ marginTop: 8 }}><strong>शुरू:</strong> {event.start_datetime}</div>
      <div style={{ marginTop: 8 }}><strong>समाप्त:</strong> {event.end_datetime}</div>
      <div style={{ marginTop: 8 }}><strong>जारी करने की तिथि:</strong> {event.issue_date}</div>
      <div style={{ marginTop: 8 }}><strong>स्थान:</strong> {event.location}</div>
      <div style={{ marginTop: 8 }}><strong>स्तर:</strong> {event.level}</div>

      <div style={{ marginTop: 12 }}>
        {photos.length > 0 && (
          <div>
            <div style={{ fontWeight: 700, marginBottom: 8 }}>Photos</div>
            <div style={{ display: "flex", gap: 8 }}>{photos.map((p, i) => <img key={i} src={p} alt={`photo-${i}`} style={{ height: 100, borderRadius: 8 }} />)}</div>
          </div>
        )}

        {media_photos.length > 0 && (
          <div style={{ marginTop: 12 }}>
            <div style={{ fontWeight: 700, marginBottom: 8 }}>Media Coverage Photos</div>
            <div style={{ display: "flex", gap: 8 }}>{media_photos.map((p, i) => <img key={i} src={p} alt={`media-${i}`} style={{ height: 100, borderRadius: 8 }} />)}</div>
          </div>
        )}

        {event.video_path && (
          <div style={{ marginTop: 12 }}>
            <div style={{ fontWeight: 700, marginBottom: 8 }}>Video</div>
            <video controls width="560" src={event.video_path} />
          </div>
        )}
      </div>

      <div style={{ marginTop: 12 }}>
        <button className="button" onClick={() => onEdit && onEdit(id)}>अपडेट</button>
      </div>
    </div>
  );
}
