const BASE = process.env.REACT_APP_API || 'http://localhost:4000/api';

export async function login(code){
  const res = await fetch(BASE + '/auth/login', {
    method: 'POST', headers: {'Content-Type':'application/json'}, body: JSON.stringify({code})
  });
  return res.json();
}

export async function listEvents(type='ongoing'){
  const res = await fetch(BASE + '/events?type=' + type);
  return res.json();
}

export async function getEvent(id){
  const res = await fetch(BASE + '/events/' + id);
  return res.json();
}

export async function markViewed(id, userId){
  const res = await fetch(BASE + '/events/' + id + '/view', {
    method:'POST', headers:{'Content-Type':'application/json'}, body: JSON.stringify({ userId })
  });
  return res.json();
}

export async function updateEvent(id, formData){
  const res = await fetch(BASE + '/events/' + id + '/update', {
    method: 'POST', body: formData
  });
  return res.json();
}

export async function addEvent(formData){
  const res = await fetch(BASE + '/events', { method:'POST', body: formData });
  return res.json();
}

export async function getReport(id){
  const res = await fetch(BASE + '/events/' + id + '/report');
  return res.json();
}
