// src/components/UpdateEvent.js
import React, { useState, useEffect, useRef } from "react";
import { getEvent, updateEvent } from "../api";

const MAX_PHOTOS = 10;
const MAX_MEDIA = 5;
const MIN_VIDEO_BYTES = 10 * 1024 * 1024;

const safeParse = (val) => {
  if (!val) return [];
  if (Array.isArray(val)) return val;
  if (typeof val === "string") {
    try {
      const parsed = JSON.parse(val);
      if (Array.isArray(parsed)) return parsed;
    } catch {}
    const t = val.trim();
    if (t.includes(",")) return t.split(",").map(s => s.trim()).filter(Boolean);
    return t ? [t] : [];
  }
  return [];
};

const UpdateEvent = ({ id, onUpdated, onBack, user }) => {
  const [form, setForm] = useState({
    name: "",
    description: "",
    start_datetime: "",
    end_datetime: "",
    issue_date: "",
    location: "",
    level: "jila",
    event_type: "",
    attendees_count: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);

  const [existingPhotos, setExistingPhotos] = useState([]);
  const [existingMediaPhotos, setExistingMediaPhotos] = useState([]);
  const [existingVideo, setExistingVideo] = useState("");

  const previewsRef = useRef([]);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    let cancelled = false;
    const loadEvent = async () => {
      try {
        const event = await getEvent(id);
        if (cancelled) return;
        setForm({
          name: event.name || "",
          description: event.description || "",
          start_datetime: event.start_datetime ? String(event.start_datetime).slice(0, 16) : "",
          end_datetime: event.end_datetime ? String(event.end_datetime).slice(0, 16) : "",
          issue_date: event.issue_date || "",
          location: event.location || "",
          level: event.level || "jila",
          event_type: event.event_type || "",
          attendees_count: event.attendees_count || "",
        });
        setExistingPhotos(safeParse(event.photos));
        setExistingMediaPhotos(safeParse(event.media_photos));
        setExistingVideo(event.video_path || "");
      } catch (err) {
        setMsg("‚ùå ‡§á‡§µ‡•á‡§Ç‡§ü ‡§≤‡•ã‡§° ‡§ï‡§∞‡§®‡•á ‡§Æ‡•á‡§Ç ‡§§‡•ç‡§∞‡•Å‡§ü‡§ø: " + (err?.message || ""));
      }
    };
    loadEvent();
    return () => { cancelled = true; };
  }, [id]);

  useEffect(() => {
    return () => {
      previewsRef.current.forEach(u => {
        try { URL.revokeObjectURL(u); } catch {}
      });
      previewsRef.current = [];
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((s) => ({ ...s, [name]: value }));
  };

  const handlePhotosChange = (e) => {
    const list = Array.from(e.target.files || []);
    if (photos.length + list.length > MAX_PHOTOS) {
      setMsg(`Photos ‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ ${MAX_PHOTOS} ‡§§‡§ï ‡§π‡•Ä ‡§ú‡•ã‡§°‡§º‡•á‡§Ç`);
      return;
    }
    setPhotos((prev) => [...prev, ...list]);
  };

  const handleMediaPhotosChange = (e) => {
    const list = Array.from(e.target.files || []);
    if (mediaPhotos.length + list.length > MAX_MEDIA) {
      setMsg(`Media Coverage Photos ‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ ${MAX_MEDIA} ‡§§‡§ï ‡§π‡•Ä ‡§ú‡•ã‡§°‡§º‡•á‡§Ç`);
      return;
    }
    setMediaPhotos((prev) => [...prev, ...list]);
  };

  const handleVideoChange = (e) => {
    const f = e.target.files[0];
    if (!f) return;
    if (f.size < MIN_VIDEO_BYTES) {
      setMsg("‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§ï‡§Æ ‡§∏‡•á ‡§ï‡§Æ 10MB ‡§π‡•ã‡§®‡§æ ‡§ö‡§æ‡§π‡§ø‡§è‡•§");
      return;
    }
    setVideo(f);
  };

  const removeExistingPhoto = (i) => setExistingPhotos(prev => prev.filter((_, idx) => idx !== i));
  const removeExistingMediaPhoto = (i) => setExistingMediaPhotos(prev => prev.filter((_, idx) => idx !== i));
  const removeExistingVideo = () => setExistingVideo("");

  const removeNewPhoto = (i) => setPhotos(prev => prev.filter((_, idx) => idx !== i));
  const removeNewMediaPhoto = (i) => setMediaPhotos(prev => prev.filter((_, idx) => idx !== i));
  const removeVideo = () => setVideo(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");

    const data = new FormData();
    Object.entries(form).forEach(([k, v]) => data.append(k, v ?? ""));

    photos.forEach(p => data.append("photos", p));
    mediaPhotos.forEach(p => data.append("mediaPhotos", p));
    if (video) data.append("video", video);

    // tell server which existing items we keep
    data.append("existingPhotos", JSON.stringify(existingPhotos));
    data.append("existingMediaPhotos", JSON.stringify(existingMediaPhotos));
    data.append("existingVideo", existingVideo || "");

    // Add user id if available (used to mark updated_by)
    if (user && user.id) data.append("userId", user.id);

    try {
      await updateEvent(id, data);
      setMsg("‚úÖ ‡§á‡§µ‡•á‡§Ç‡§ü ‡§∏‡§´‡§≤‡§§‡§æ‡§™‡•Ç‡§∞‡•ç‡§µ‡§ï ‡§Ö‡§™‡§°‡•á‡§ü ‡§ï‡§ø‡§Ø‡§æ ‡§ó‡§Ø‡§æ");
      if (onUpdated) onUpdated();
    } catch (err) {
      setMsg("‚ùå ‡§§‡•ç‡§∞‡•Å‡§ü‡§ø: " + (err?.message || "‡§∏‡§∞‡•ç‡§µ‡§∞ ‡§∏‡§Æ‡§∏‡•ç‡§Ø‡§æ"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="update-event-container max-w-3xl mx-auto bg-white p-6 rounded-xl shadow">
      <div style={{ display: "flex", gap: 8, alignItems: "center", marginBottom: 12 }}>
        {onBack && <button className="button ghost" onClick={onBack}>‚Üê ‡§µ‡§æ‡§™‡§∏</button>}
        <h2 className="text-xl font-semibold mb-4 text-center" style={{ margin: 0 }}>üìù ‡§á‡§µ‡•á‡§Ç‡§ü ‡§Ö‡§™‡§°‡•á‡§ü ‡§ï‡§∞‡•á‡§Ç</h2>
      </div>

      <form onSubmit={handleSubmit} className="space-y-3">
        <div>
          <label>‡§á‡§µ‡•á‡§Ç‡§ü ‡§ï‡§æ ‡§®‡§æ‡§Æ</label>
          <input name="name" value={form.name} onChange={handleChange} className="input" required />
        </div>

        <div>
          <label>‡§µ‡§ø‡§µ‡§∞‡§£</label>
          <textarea name="description" value={form.description} onChange={handleChange} className="input" rows="3" />
        </div>

        <div>
          <label>‡§™‡•ç‡§∞‡§æ‡§∞‡§Ç‡§≠ ‡§¶‡§ø‡§®‡§æ‡§Ç‡§ï / ‡§∏‡§Æ‡§Ø</label>
          <input type="datetime-local" name="start_datetime" value={form.start_datetime} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§∏‡§Æ‡§æ‡§™‡§® ‡§¶‡§ø‡§®‡§æ‡§Ç‡§ï / ‡§∏‡§Æ‡§Ø</label>
          <input type="datetime-local" name="end_datetime" value={form.end_datetime} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§ú‡§æ‡§∞‡•Ä ‡§ï‡§∞‡§®‡•á ‡§ï‡•Ä ‡§¶‡§ø‡§®‡§æ‡§Ç‡§ï</label>
          <input type="date" name="issue_date" value={form.issue_date} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§∏‡•ç‡§•‡§æ‡§®</label>
          <input name="location" value={form.location} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§∏‡•ç‡§§‡§∞</label>
          <select name="level" value={form.level} onChange={handleChange} className="input">
            <option value="jila">‡§ú‡§ø‡§≤‡§æ</option>
            <option value="block">‡§¨‡•ç‡§≤‡•â‡§ï</option>
          </select>
        </div>

        <div>
          <label>‡§ï‡§æ‡§∞‡•ç‡§Ø‡§ï‡•ç‡§∞‡§Æ ‡§™‡•ç‡§∞‡§ï‡§æ‡§∞</label>
          <input name="event_type" value={form.event_type} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§π‡§æ‡§ú‡§º‡§ø‡§∞‡•Ä ‡§ï‡•Ä ‡§∏‡§Ç‡§ñ‡•ç‡§Ø‡§æ</label>
          <input type="number" name="attendees_count" value={form.attendees_count} onChange={handleChange} className="input" min="0" />
        </div>

        {/* Existing Photos */}
        {existingPhotos.length > 0 && (
          <div>
            <label>‡§Æ‡•å‡§ú‡•Ç‡§¶‡§æ ‡§´‡§º‡•ã‡§ü‡•ã</label>
            <div className="flex flex-wrap gap-2 mt-2">
              {existingPhotos.map((p, i) => (
                <div key={i} className="relative">
                  <img src={p} alt="" className="w-20 h-20 object-cover rounded" />
                  <button type="button" onClick={() => removeExistingPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                    ‚úï
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* New Photos */}
        <div>
          <label>‡§®‡§à Photos (‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ {MAX_PHOTOS})</label>
          <input type="file" multiple accept="image/*" onChange={handlePhotosChange} />
          <div className="flex flex-wrap gap-2 mt-2">
            {photos.map((p, i) => (
              <div key={i} className="relative">
                <img src={URL.createObjectURL(p)} alt="" className="w-20 h-20 object-cover rounded" />
                <button type="button" onClick={() => removeNewPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                  ‚úï
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Existing Media Photos */}
        {existingMediaPhotos.length > 0 && (
          <div>
            <label>‡§Æ‡•å‡§ú‡•Ç‡§¶‡§æ ‡§Æ‡•Ä‡§°‡§ø‡§Ø‡§æ ‡§ï‡§µ‡§∞‡•á‡§ú ‡§´‡§º‡•ã‡§ü‡•ã</label>
            <div className="flex flex-wrap gap-2 mt-2">
              {existingMediaPhotos.map((p, i) => (
                <div key={i} className="relative">
                  <img src={p} alt="" className="w-20 h-20 object-cover rounded" />
                  <button type="button" onClick={() => removeExistingMediaPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                    ‚úï
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* New Media Photos */}
        <div>
          <label>‡§®‡§à Media Coverage Photos (‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ {MAX_MEDIA})</label>
          <input type="file" multiple accept="image/*" onChange={handleMediaPhotosChange} />
          <div className="flex flex-wrap gap-2 mt-2">
            {mediaPhotos.map((p, i) => (
              <div key={i} className="relative">
                <img src={URL.createObjectURL(p)} alt="" className="w-20 h-20 object-cover rounded" />
                <button type="button" onClick={() => removeNewMediaPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                  ‚úï
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Existing Video */}
        {existingVideo && (
          <div>
            <label>‡§Æ‡•å‡§ú‡•Ç‡§¶‡§æ ‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã</label>
            <div className="mt-2 relative">
              <video src={existingVideo} controls className="w-full rounded"></video>
              <button type="button" onClick={removeExistingVideo} className="absolute top-2 right-2 bg-red-500 text-white rounded px-2 py-1">
                ‚úï
              </button>
            </div>
          </div>
        )}

        {/* New Video */}
        <div>
          <label>‡§®‡§à ‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã (‡§ï‡§Æ ‡§∏‡•á ‡§ï‡§Æ 10MB)</label>
          <input type="file" accept="video/*" onChange={handleVideoChange} />
          {video && (
            <div className="mt-2 relative">
              <video src={URL.createObjectURL(video)} controls className="w-full rounded"></video>
              <button type="button" onClick={removeVideo} className="absolute top-2 right-2 bg-red-500 text-white rounded px-2 py-1">
                ‚úï
              </button>
            </div>
          )}
        </div>

        <div className="form-actions">
          <button disabled={loading} className="btn w-full button secondary">
            {loading ? "‚è≥ ‡§Ö‡§™‡§°‡•á‡§ü ‡§ï‡§∞ ‡§∞‡§π‡§æ ‡§π‡•à..." : "‡§Ö‡§™‡§°‡•á‡§ü ‡§ï‡§∞‡•á‡§Ç"}
          </button>
        </div>

        {msg && <p className="text-center text-sm mt-2">{msg}</p>}
      </form>
    </div>
  );
};

export default UpdateEvent;
