import React, { useEffect, useState } from 'react';
import { getEvent, markViewed } from '../api';

export default function EventDetails({ id, user, onBack, onEdit }){
  const [event, setEvent] = useState(null);
  useEffect(()=>{ load(); }, [id]);
  async function load(){
    const e = await getEvent(id);
    setEvent(e);
    await markViewed(id, user.id);
  }
  if (!event) return <div>लोड हो रहा है...</div>;
  return (
    <div>
      <button className='button secondary' onClick={onBack}>वापस</button>
      <h2>{event.name}</h2>
      <div><strong>विवरण:</strong> {event.description}</div>
      <div><strong>शुरू:</strong> {event.start_datetime}</div>
      <div><strong>समाप्त:</strong> {event.end_datetime}</div>
      <div><strong>जारी करने की तिथि:</strong> {event.issue_date}</div>
      <div><strong>स्थान:</strong> {event.location}</div>
      <div><strong>स्तर:</strong> {event.level}</div>
      <div style={{marginTop:10}}>
        {event.photos && JSON.parse(event.photos || '[]').map((p,i)=>(<img key={i} src={p} alt='' style={{height:80, marginRight:6}} />))}
      </div>
      <div style={{marginTop:10}}>
        {event.media_photos && JSON.parse(event.media_photos || '[]').map((p,i)=>(<img key={i} src={p} alt='' style={{height:80, marginRight:6}} />))}
      </div>
      <div style={{marginTop:10}}>
        {event.video_path && <div><strong>वीडियो:</strong><br /><video controls width='400' src={event.video_path} /></div>}
      </div>
      <div style={{marginTop:10}}>
        <button className='button' onClick={()=>onEdit(id)}>अपडेट</button>
      </div>
    </div>
  );
}
