// src/components/EventDetails.js
import React, { useEffect, useState } from "react";
import { getEvent, markViewed } from "../api";

function keyMonthViews(userId, monthKey) {
  return `inc_user_${userId}_views_${monthKey}`;
}

export default function EventDetails({ id, user, onBack, onEdit }) {
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, [id]);

  async function load() {
    setLoading(true);
    try {
      const e = await getEvent(id);
      setEvent(e || null);

      // mark viewed
      try {
        if (user && user.id) {
          await markViewed(id, user.id);
          const d = new Date();
          const monthKey = `${d.getFullYear()}-${String(d.getMonth() + 1).padStart(2, "0")}`;
          const k = keyMonthViews(user.id, monthKey);
          const cur = parseInt(localStorage.getItem(k) || "0", 10) || 0;
          localStorage.setItem(k, String(cur + 1));
        }
      } catch (err) {
        console.error("markViewed failed", err);
      }
    } catch (err) {
      console.error("getEvent failed", err);
      setEvent(null);
    } finally {
      setLoading(false);
    }
  }

  if (loading) return <div className="canvas">लोड हो रहा है...</div>;
  if (!event) return <div className="canvas">इवेंट नहीं मिला</div>;

  function safeParse(val) {
    if (!val) return [];
    if (Array.isArray(val)) return val;
    if (typeof val === "string") {
      const t = val.trim();
      if (!t) return [];
      if (t.startsWith("[")) {
        try { const p = JSON.parse(t); if (Array.isArray(p)) return p; } catch {}
      }
      if (t.includes(",")) return t.split(",").map(s=>s.trim()).filter(Boolean);
      return [t];
    }
    return [];
  }

  function makeSrc(p) {
    if (!p) return p;
    if (p.startsWith("http://") || p.startsWith("https://")) return p;
    return `/uploads/${p}`;
  }

  function fmtDateTime(v) {
    if (!v) return "—";
    let d = new Date(v);
    if (isNaN(d)) {
      try { d = new Date(String(v).replace(" ", "T")); } catch { d = null; }
    }
    if (!d || isNaN(d)) return String(v);
    const opts = { year: "numeric", month: "short", day: "numeric", hour: "2-digit", minute: "2-digit" };
    return d.toLocaleString("hi-IN", opts);
  }

  const photos = safeParse(event.photos);
  const media_photos = safeParse(event.media_photos);

  return (
    <div className="canvas">
      <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
        <button className="button ghost" onClick={onBack}>वापस</button>
        <h2 style={{ marginTop: 12, marginBottom: 0 }}>{event.name}</h2>
        <div style={{ marginLeft: "auto" }}>
          <button className="button" onClick={() => onEdit && onEdit(id)}>अपडेट</button>
        </div>
      </div>

      <div style={{ marginTop: 12 }}>
        <div><strong>विवरण:</strong></div>
        <div style={{ marginTop: 6 }}>{event.description || "—"}</div>

        <div style={{ marginTop: 12 }}><strong>शुरू:</strong> {fmtDateTime(event.start_datetime)}</div>
        <div style={{ marginTop: 6 }}><strong>समाप्त:</strong> {fmtDateTime(event.end_datetime)}</div>
        <div style={{ marginTop: 6 }}><strong>जारी करने की तिथि:</strong> {event.issue_date ? new Date(event.issue_date).toLocaleDateString("hi-IN") : "—"}</div>

        <div style={{ marginTop: 8 }}><strong>स्थान:</strong> {event.location || "—"}</div>
        <div style={{ marginTop: 6 }}><strong>स्तर:</strong> {event.level || "—"}</div>
        <div style={{ marginTop: 6 }}><strong>कार्यक्रम प्रकार:</strong> {event.event_type || "—"}</div>
        <div style={{ marginTop: 6 }}><strong>हाज़िरी की संख्या:</strong> {event.attendees_count || 0}</div>

        {photos.length > 0 && (
          <div style={{ marginTop: 14 }}>
            <div style={{ fontWeight: 700, marginBottom: 8 }}>Photos</div>
            <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
              {photos.map((p, i) => (
                <div key={i} style={{ width: 120 }}>
                  <img src={makeSrc(p)} alt={`photo-${i}`} style={{ height: 90, width: "100%", objectFit: "cover", borderRadius: 8 }} />
                </div>
              ))}
            </div>
          </div>
        )}

        {media_photos.length > 0 && (
          <div style={{ marginTop: 12 }}>
            <div style={{ fontWeight: 700, marginBottom: 8 }}>Media Coverage Photos</div>
            <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
              {media_photos.map((p, i) => (
                <div key={i} style={{ width: 120 }}>
                  <img src={makeSrc(p)} alt={`media-${i}`} style={{ height: 90, width: "100%", objectFit: "cover", borderRadius: 8 }} />
                </div>
              ))}
            </div>
          </div>
        )}

        {event.video_path && (
          <div style={{ marginTop: 12 }}>
            <div style={{ fontWeight: 700, marginBottom: 8 }}>Video</div>
            <video controls width="560" src={makeSrc(event.video_path)} />
          </div>
        )}
      </div>
    </div>
  );
}
