import React, { useState } from "react";
import { addEvent } from "../api";

export default function AddEvent() {
  const [form, setForm] = useState({
    name: "",
    description: "",
    startDate: "",
    endDate: "",
    issueDate: "",
    eventType: "Meeting",
    location: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // --- File Handling ---
  const handlePhotoChange = (e) => {
    const files = Array.from(e.target.files);
    const newFiles = [...photos, ...files].slice(0, 10); // limit 10
    setPhotos(newFiles);
  };

  const handleRemovePhoto = (index) => {
    const updated = photos.filter((_, i) => i !== index);
    setPhotos(updated);
  };

  const handleMediaPhotoChange = (e) => {
    const files = Array.from(e.target.files);
    const newFiles = [...mediaPhotos, ...files].slice(0, 5); // limit 5
    setMediaPhotos(newFiles);
  };

  const handleRemoveMediaPhoto = (index) => {
    const updated = mediaPhotos.filter((_, i) => i !== index);
    setMediaPhotos(updated);
  };

  const handleVideoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const sizeMB = file.size / (1024 * 1024);
      if (sizeMB < 10) {
        setMessage("वीडियो कम से कम 10MB का होना चाहिए।");
        e.target.value = "";
        return;
      }
      if (sizeMB > 200) {
        setMessage("वीडियो 200MB से अधिक नहीं होना चाहिए।");
        e.target.value = "";
        return;
      }
      setVideo(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const fd = new FormData();
      for (const key in form) fd.append(key, form[key]);
      photos.forEach((file) => fd.append("photos", file));
      mediaPhotos.forEach((file) => fd.append("mediaPhotos", file));
      if (video) fd.append("video", video);

      const res = await addEvent(fd);

      if (res.error) {
        setMessage("त्रुटि: " + res.error);
      } else {
        setMessage("कार्यक्रम सफलतापूर्वक जोड़ा गया।");
        setForm({
          name: "",
          description: "",
          startDate: "",
          endDate: "",
          issueDate: "",
          eventType: "Meeting",
          location: "",
        });
        setPhotos([]);
        setMediaPhotos([]);
        setVideo(null);
      }
    } catch (err) {
      setMessage("त्रुटि: सर्वर से संपर्क नहीं हो सका।");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <main>
        <h2>नया कार्यक्रम जोड़ें</h2>
        {message && <div className="notice">{message}</div>}

        <form onSubmit={handleSubmit}>
          <div>
            <label>कार्यक्रम का नाम</label>
            <input
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>विवरण</label>
            <textarea
              name="description"
              value={form.description}
              onChange={handleChange}
              required
            />
          </div>

          <div className="row">
            <div className="col">
              <label>प्रारंभ दिनांक / समय</label>
              <input
                type="datetime-local"
                name="startDate"
                value={form.startDate}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col">
              <label>समापन दिनांक / समय</label>
              <input
                type="datetime-local"
                name="endDate"
                value={form.endDate}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div>
            <label>जारी करने की दिनांक</label>
            <input
              type="date"
              name="issueDate"
              value={form.issueDate}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>स्थान</label>
            <input
              type="text"
              name="location"
              value={form.location}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label>कार्यक्रम प्रकार</label>
            <select
              name="eventType"
              value={form.eventType}
              onChange={handleChange}
            >
              <option>धरणा</option>
              <option>मीटिंग</option>
              <option>बंद</option>
              <option>रैली</option>
              <option>सभा</option>
              <option>ग्यापन</option>
            </select>
          </div>

          {/* --- Photo Upload --- */}
          <div>
            <label>फ़ोटो (अधिकतम 10)</label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handlePhotoChange}
            />
            <div className="event-thumbs row">
              {photos.map((p, i) => (
                <div key={i} style={{ position: "relative" }}>
                  <img
                    src={URL.createObjectURL(p)}
                    alt="preview"
                    height="70"
                    style={{ marginRight: 8 }}
                  />
                  <button
                    type="button"
                    onClick={() => handleRemovePhoto(i)}
                    style={{
                      position: "absolute",
                      top: 0,
                      right: 0,
                      background: "red",
                      color: "white",
                      border: "none",
                      borderRadius: "50%",
                      width: "20px",
                      height: "20px",
                      cursor: "pointer",
                    }}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* --- Media Coverage Photos --- */}
          <div>
            <label>मीडिया कवरेज फ़ोटो (अधिकतम 5)</label>
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handleMediaPhotoChange}
            />
            <div className="event-thumbs row">
              {mediaPhotos.map((p, i) => (
                <div key={i} style={{ position: "relative" }}>
                  <img
                    src={URL.createObjectURL(p)}
                    alt="media"
                    height="70"
                    style={{ marginRight: 8 }}
                  />
                  <button
                    type="button"
                    onClick={() => handleRemoveMediaPhoto(i)}
                    style={{
                      position: "absolute",
                      top: 0,
                      right: 0,
                      background: "red",
                      color: "white",
                      border: "none",
                      borderRadius: "50%",
                      width: "20px",
                      height: "20px",
                      cursor: "pointer",
                    }}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* --- Video --- */}
          <div>
            <label>वीडियो (कम से कम 10MB, अधिकतम 200MB)</label>
            <input type="file" accept="video/*" onChange={handleVideoChange} />
            {video && (
              <video
                controls
                src={URL.createObjectURL(video)}
                width="200"
                style={{ marginTop: 10 }}
              />
            )}
          </div>

          <div className="form-actions">
            <button className="button" disabled={loading}>
              {loading ? "जोड़ा जा रहा है..." : "कार्यक्रम जोड़ें"}
            </button>
          </div>
        </form>
      </main>
    </div>
  );
}
