// src/components/AddEvent.js
import React, { useState, useEffect, useRef } from "react";
import { addEvent } from "../api";

const MAX_PHOTOS = 10;
const MAX_MEDIA = 5;
const MIN_VIDEO_BYTES = 10 * 1024 * 1024;

const AddEvent = ({ onAdded, onBack, user }) => {
  const [form, setForm] = useState({
    name: "",
    description: "",
    start_datetime: "",
    end_datetime: "",
    issue_date: "",
    location: "",
    level: "jila",
    event_type: "",
    attendees_count: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  // store created preview objectURLs to revoke them on cleanup
  const previewsRef = useRef([]);

  useEffect(() => {
    return () => {
      // cleanup created object URLs
      previewsRef.current.forEach((u) => {
        try { URL.revokeObjectURL(u); } catch {}
      });
      previewsRef.current = [];
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const addPreview = (file) => {
    const url = URL.createObjectURL(file);
    previewsRef.current.push(url);
    return url;
  };

  const handlePhotosChange = (e) => {
    const list = Array.from(e.target.files || []);
    if (photos.length + list.length > MAX_PHOTOS) {
      setMsg(`Photos ‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ ${MAX_PHOTOS} ‡§§‡§ï ‡§π‡•Ä ‡§ú‡•ã‡§°‡§º‡•á‡§Ç`);
      return;
    }
    setMsg("");
    setPhotos((prev) => [...prev, ...list]);
  };

  const handleMediaPhotosChange = (e) => {
    const list = Array.from(e.target.files || []);
    if (mediaPhotos.length + list.length > MAX_MEDIA) {
      setMsg(`Media Coverage Photos ‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ ${MAX_MEDIA} ‡§§‡§ï ‡§π‡•Ä ‡§ú‡•ã‡§°‡§º‡•á‡§Ç`);
      return;
    }
    setMsg("");
    setMediaPhotos((prev) => [...prev, ...list]);
  };

  const handleVideoChange = (e) => {
    const f = e.target.files[0];
    if (!f) return;
    if (f.size < MIN_VIDEO_BYTES) {
      setMsg("‡§µ‡•Ä‡§°‡§ø‡§Ø‡•ã ‡§ï‡§Æ ‡§∏‡•á ‡§ï‡§Æ 10MB ‡§π‡•ã‡§®‡§æ ‡§ö‡§æ‡§π‡§ø‡§è‡•§");
      return;
    }
    setMsg("");
    setVideo(f);
  };

  const removePhoto = (index) => {
    setPhotos((prev) => prev.filter((_, i) => i !== index));
  };

  const removeMediaPhoto = (index) => {
    setMediaPhotos((prev) => prev.filter((_, i) => i !== index));
  };

  const removeVideo = () => {
    setVideo(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");

    const data = new FormData();
