// src/components/UpdateEvent.js
import React, { useState, useEffect } from "react";
import { getEvent, updateEvent } from "../api";

const UpdateEvent = ({ eventId, onUpdated }) => {
  const [form, setForm] = useState({
    name: "",
    description: "",
    start_datetime: "",
    end_datetime: "",
    issue_date: "",
    location: "",
    level: "jila",
    event_type: "",
    attendees_count: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);
  const [existingPhotos, setExistingPhotos] = useState([]);
  const [existingMediaPhotos, setExistingMediaPhotos] = useState([]);
  const [existingVideo, setExistingVideo] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  useEffect(() => {
    const loadEvent = async () => {
      try {
        const { data } = await getEvent(eventId);
        const event = data.event || {};
        setForm({
          name: event.name || "",
          description: event.description || "",
          start_datetime: event.start_datetime?.slice(0, 16) || "",
          end_datetime: event.end_datetime?.slice(0, 16) || "",
          issue_date: event.issue_date || "",
          location: event.location || "",
          level: event.level || "jila",
          event_type: event.event_type || "",
          attendees_count: event.attendees_count || "",
        });
        setExistingPhotos(JSON.parse(event.photos || "[]"));
        setExistingMediaPhotos(JSON.parse(event.media_photos || "[]"));
        setExistingVideo(event.video_path || "");
      } catch (err) {
        setMsg("❌ इवेंट लोड करने में त्रुटि");
      }
    };
    loadEvent();
  }, [eventId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handlePhotosChange = (e) => setPhotos([...e.target.files]);
  const handleMediaPhotosChange = (e) => setMediaPhotos([...e.target.files]);
  const handleVideoChange = (e) => setVideo(e.target.files[0]);

  const removeExistingPhoto = (i) => setExistingPhotos((prev) => prev.filter((_, index) => index !== i));
  const removeExistingMediaPhoto = (i) => setExistingMediaPhotos((prev) => prev.filter((_, index) => index !== i));
  const removeExistingVideo = () => setExistingVideo("");

  const removeNewPhoto = (i) => setPhotos((prev) => prev.filter((_, index) => index !== i));
  const removeNewMediaPhoto = (i) => setMediaPhotos((prev) => prev.filter((_, index) => index !== i));
  const removeVideo = () => setVideo(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");

    const data = new FormData();
    Object.entries(form).forEach(([key, value]) => data.append(key, value));
    photos.forEach((p) => data.append("photos", p));
    mediaPhotos.forEach((p) => data.append("mediaPhotos", p));
    if (video) data.append("video", video);

    // existing ones you’re keeping
    data.append("existingPhotos", JSON.stringify(existingPhotos));
    data.append("existingMediaPhotos", JSON.stringify(existingMediaPhotos));
    data.append("existingVideo", existingVideo);

    try {
      await updateEvent(eventId, data);
      setMsg("✅ इवेंट सफलतापूर्वक अपडेट किया गया");
      if (onUpdated) onUpdated();
    } catch (err) {
      setMsg("❌ त्रुटि: " + (err?.response?.data?.error || "सर्वर समस्या"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="update-event-container max-w-3xl mx-auto bg-white p-6 rounded-xl shadow">
      <h2 className="text-xl font-semibold mb-4 text-center">📝 इवेंट अपडेट करें</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <div>
          <label>इवेंट का नाम</label>
          <input name="name" value={form.name} onChange={handleChange} className="input" required />
        </div>

        <div>
          <label>विवरण</label>
          <textarea name="description" value={form.description} onChange={handleChange} className="input" rows="3" />
        </div>

        <div>
          <label>प्रारंभ दिनांक / समय</label>
          <input type="datetime-local" name="start_datetime" value={form.start_datetime} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>समापन दिनांक / समय</label>
          <input type="datetime-local" name="end_datetime" value={form.end_datetime} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>जारी करने की दिनांक</label>
          <input type="date" name="issue_date" value={form.issue_date} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>स्थान</label>
          <input name="location" value={form.location} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>स्तर</label>
          <select name="level" value={form.level} onChange={handleChange} className="input">
            <option value="jila">जिला</option>
            <option value="block">ब्लॉक</option>
          </select>
        </div>

        <div>
          <label>कार्यक्रम प्रकार</label>
          <input name="event_type" value={form.event_type} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>हाज़िरी की संख्या</label>
          <input type="number" name="attendees_count" value={form.attendees_count} onChange={handleChange} className="input" min="0" />
        </div>

        {/* Existing Photos */}
        {existingPhotos.length > 0 && (
          <div>
            <label>मौजूदा फ़ोटो</label>
            <div className="flex flex-wrap gap-2 mt-2">
              {existingPhotos.map((p, i) => (
                <div key={i} className="relative">
                  <img src={p} alt="" className="w-20 h-20 object-cover rounded" />
                  <button type="button" onClick={() => removeExistingPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                    ✕
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* New Photos */}
        <div>
          <label>नई Photos (अधिकतम 10)</label>
          <input type="file" multiple accept="image/*" onChange={handlePhotosChange} />
          <div className="flex flex-wrap gap-2 mt-2">
            {photos.map((p, i) => (
              <div key={i} className="relative">
                <img src={URL.createObjectURL(p)} alt="" className="w-20 h-20 object-cover rounded" />
                <button type="button" onClick={() => removeNewPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                  ✕
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Existing Media Photos */}
        {existingMediaPhotos.length > 0 && (
          <div>
            <label>मौजूदा मीडिया कवरेज फ़ोटो</label>
            <div className="flex flex-wrap gap-2 mt-2">
              {existingMediaPhotos.map((p, i) => (
                <div key={i} className="relative">
                  <img src={p} alt="" className="w-20 h-20 object-cover rounded" />
                  <button type="button" onClick={() => removeExistingMediaPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                    ✕
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* New Media Photos */}
        <div>
          <label>नई Media Coverage Photos (अधिकतम 5)</label>
          <input type="file" multiple accept="image/*" onChange={handleMediaPhotosChange} />
          <div className="flex flex-wrap gap-2 mt-2">
            {mediaPhotos.map((p, i) => (
              <div key={i} className="relative">
                <img src={URL.createObjectURL(p)} alt="" className="w-20 h-20 object-cover rounded" />
                <button type="button" onClick={() => removeNewMediaPhoto(i)} className="absolute top-0 right-0 bg-red-500 text-white rounded-full px-1">
                  ✕
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Existing Video */}
        {existingVideo && (
          <div>
            <label>मौजूदा वीडियो</label>
            <div className="mt-2 relative">
              <video src={existingVideo} controls className="w-full rounded"></video>
              <button type="button" onClick={removeExistingVideo} className="absolute top-2 right-2 bg-red-500 text-white rounded px-2 py-1">
                ✕
              </button>
            </div>
          </div>
        )}

        {/* New Video */}
        <div>
          <label>नई वीडियो (कम से कम 10MB)</label>
          <input type="file" accept="video/*" onChange={handleVideoChange} />
          {video && (
            <div className="mt-2 relative">
              <video src={URL.createObjectURL(video)} controls className="w-full rounded"></video>
              <button type="button" onClick={removeVideo} className="absolute top-2 right-2 bg-red-500 text-white rounded px-2 py-1">
                ✕
              </button>
            </div>
          )}
        </div>

        <button disabled={loading} className="btn w-full">
          {loading ? "⏳ अपडेट कर रहा है..." : "अपडेट करें"}
        </button>

        {msg && <p className="text-center text-sm mt-2">{msg}</p>}
      </form>
    </div>
  );
};

export default UpdateEvent;
