import React, { useState } from 'react';
import { login } from '../api';

export default function Login({ onLogin }){
  const [code, setCode] = useState('');
  const [err, setErr] = useState(null);
  const doLogin = async ()=>{
    setErr(null);
    if (code.length !== 4) { setErr('कृपया 4 अंकों का कोड दर्ज करें।'); return; }
    const r = await login(code);
    if (r.error) setErr(r.error);
    else { onLogin(r.user); }
  };
  return (
    <div style={{maxWidth:400, margin:'80px auto', background:'#fff', padding:20, borderRadius:8}}>
      <h2>लॉगिन</h2>
      <p>कृपया 4-अंकीय कोड दर्ज करें</p>
      <input value={code} onChange={e=>setCode(e.target.value)} placeholder='उदा. 1111' style={{padding:10, width:'100%'}} />
      {err && <div style={{color:'red', marginTop:8}}>{err}</div>}
      <div style={{marginTop:12}}>
        <button className='button' onClick={doLogin}>लॉगिन</button>
      </div>
      <div style={{marginTop:8, color:'#555'}}><small>डे़मो कोड: 1111 (admin), 2222 (user)</small></div>
    </div>
  );
}
