// src/components/AddEvent.js
import React, { useState } from "react";
import { addEvent } from "../api";
import { useNavigate } from "react-router-dom";

export default function AddEvent({ user, onBack }) {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    description: "",
    startDate: "",
    endDate: "",
    issueDate: "",
    eventType: "मीटिंग",
    location: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  // --- Normal Photos ---
  const handlePhotoChange = (e) => {
    const files = Array.from(e.target.files || []);
    if (photos.length + files.length > 10) {
      setMessage("आप अधिकतम 10 फ़ोटो ही चुन सकते हैं।");
      return;
    }
    setPhotos([...photos, ...files]);
  };

  const handleRemovePhoto = (index) => {
    const updated = photos.filter((_, i) => i !== index);
    setPhotos(updated);
  };

  // --- Media Coverage Photos ---
  const handleMediaPhotoChange = (e) => {
    const files = Array.from(e.target.files || []);
    if (mediaPhotos.length + files.length > 5) {
      setMessage("आप अधिकतम 5 मीडिया फ़ोटो ही चुन सकते हैं।");
      return;
    }
    setMediaPhotos([...mediaPhotos, ...files]);
  };

  const handleRemoveMediaPhoto = (index) => {
    const updated = mediaPhotos.filter((_, i) => i !== index);
    setMediaPhotos(updated);
  };

  // --- Video Validation ---
  const handleVideoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const sizeMB = file.size / (1024 * 1024);
      if (sizeMB < 10) {
        setMessage("वीडियो कम से कम 10MB का होना चाहिए।");
        e.target.value = "";
        return;
      }
      if (sizeMB > 200) {
        setMessage("वीडियो 200MB से अधिक नहीं होना चाहिए।");
        e.target.value = "";
        return;
      }
      setVideo(file);
    }
  };

  // --- Submit Form ---
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    // Basic frontend validation
    if (!form.name || !form.description || !form.startDate || !form.endDate || !form.issueDate || !form.location) {
      setMessage("कृपया सभी आवश्यक फ़ील्ड भरें।");
      setLoading(false);
      return;
    }

    try {
      const fd = new FormData();
      // Map frontend fields to backend expected keys
      fd.append("name", form.name);
      fd.append("description", form.description);
      fd.append("start_datetime", form.startDate);
      fd.append("end_datetime", form.endDate);
      fd.append("issue_date", form.issueDate);
      fd.append("location", form.location);

      // level/event_type - send both to be safe (backend accepts either)
      fd.append("level", form.eventType);
      fd.append("event_type", form.eventType);

      // created_by if available
      if (user && user.id) fd.append("created_by", user.id);

      photos.forEach((file) => fd.append("photos", file));
      // multer expects "mediaPhotos" (camelCase) in routes/events.js -> upload.fields(...)
      mediaPhotos.forEach((file) => fd.append("mediaPhotos", file));
      if (video) fd.append("video", video);

      const res = await addEvent(fd);

      if (res.error) {
        setMessage("त्रुटि: " + res.error);
      } else if (res.id) {
        setMessage("कार्यक्रम सफलतापूर्वक जोड़ा गया।");
        // reset form
        setForm({
          name: "",
          description: "",
          startDate: "",
          endDate: "",
          issueDate: "",
          eventType: "मीटिंग",
          location: "",
        });
        setPhotos([]);
        setMediaPhotos([]);
        setVideo(null);
        // optionally go back
        if (onBack) setTimeout(() => onBack(), 800);
        else setTimeout(() => navigate("/"), 1000);
      } else {
        setMessage("सर्वर से अप्रत्याशित उत्तर मिला।");
      }
    } catch (err) {
      setMessage(`त्रुटि: ${err.message || "सर्वर से संपर्क नहीं हो सका।"}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <main>
        <h2>नया कार्यक्रम जोड़ें</h2>
        {message && <div className="notice">{message}</div>}

        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div>
            <label htmlFor="name">कार्यक्रम का नाम</label>
            <input
              id="name"
              type="text"
              name="name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label htmlFor="description">विवरण</label>
            <textarea
              id="description"
              name="description"
              value={form.description}
              onChange={handleChange}
              required
            />
          </div>

          <div className="row">
            <div className="col">
              <label htmlFor="startDate">प्रारंभ दिनांक / समय</label>
              <input
                id="startDate"
                type="datetime-local"
                name="startDate"
                value={form.startDate}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col">
              <label htmlFor="endDate">समापन दिनांक / समय</label>
              <input
                id="endDate"
                type="datetime-local"
                name="endDate"
                value={form.endDate}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div>
            <label htmlFor="issueDate">जारी करने की दिनांक</label>
            <input
              id="issueDate"
              type="date"
              name="issueDate"
              value={form.issueDate}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label htmlFor="location">स्थान</label>
            <input
              id="location"
              type="text"
              name="location"
              value={form.location}
              onChange={handleChange}
              required
            />
          </div>

          <div>
            <label htmlFor="eventType">कार्यक्रम प्रकार / स्तर</label>
            <select
              id="eventType"
              name="eventType"
              value={form.eventType}
              onChange={handleChange}
            >
              <option>धरणा</option>
              <option>मीटिंग</option>
              <option>बंद</option>
              <option>रैली</option>
              <option>सभा</option>
              <option>ग्यापन</option>
            </select>
          </div>

          {/* Photos */}
          <div>
            <label>फ़ोटो (अधिकतम 10)</label>
            <input type="file" multiple accept="image/*" onChange={handlePhotoChange} />
            <div className="event-thumbs row">
              {photos.map((p, i) => (
                <div key={i} style={{ position: "relative" }}>
                  <img src={URL.createObjectURL(p)} alt="preview" height="70" style={{ marginRight: 8 }} />
                  <button
                    type="button"
                    onClick={() => handleRemovePhoto(i)}
                    style={{
                      position: "absolute",
                      top: 0,
                      right: 0,
                      background: "red",
                      color: "white",
                      border: "none",
                      borderRadius: "50%",
                      width: "20px",
                      height: "20px",
                      cursor: "pointer",
                    }}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Media Photos */}
          <div>
            <label>मीडिया कवरेज फ़ोटो (अधिकतम 5)</label>
            <input type="file" multiple accept="image/*" onChange={handleMediaPhotoChange} />
            <div className="event-thumbs row">
              {mediaPhotos.map((p, i) => (
                <div key={i} style={{ position: "relative" }}>
                  <img src={URL.createObjectURL(p)} alt="media" height="70" style={{ marginRight: 8 }} />
                  <button
                    type="button"
                    onClick={() => handleRemoveMediaPhoto(i)}
                    style={{
                      position: "absolute",
                      top: 0,
                      right: 0,
                      background: "red",
                      color: "white",
                      border: "none",
                      borderRadius: "50%",
                      width: "20px",
                      height: "20px",
                      cursor: "pointer",
                    }}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Video */}
          <div>
            <label>वीडियो (कम से कम 10MB, अधिकतम 200MB)</label>
            <input type="file" accept="video/*" onChange={handleVideoChange} />
            {video && (
              <video controls src={URL.createObjectURL(video)} width="200" style={{ marginTop: 10 }} />
            )}
          </div>

          <div className="form-actions">
            <button className="button" disabled={loading}>
              {loading ? "जोड़ा जा रहा है..." : "कार्यक्रम जोड़ें"}
            </button>
            <button type="button" className="button ghost" onClick={() => (onBack ? onBack() : navigate(-1))}>
              रद्द करें
            </button>
          </div>
        </form>
      </main>
    </div>
  );
}
