import React, { useState, useEffect } from 'react';
import { listEvents } from '../api';

/*
  Props expected:
    - onOpen(id)      // show details
    - onEdit(id)      // edit event
    - onAdd()         // add event
    - onReport(id)    // report
    - onLogout()
    - user
*/

export default function Home({ onOpen, onEdit, onAdd, onReport, onLogout, user }) {
  const [type, setType] = useState('ongoing'); // 'ongoing' or 'previous'
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState('');

  useEffect(() => {
    fetchEvents(type);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [type]);

  async function fetchEvents(t) {
    setLoading(true);
    try {
      const res = await listEvents(t);
      setEvents(Array.isArray(res) ? res : []);
    } catch (err) {
      console.error('Failed to load events', err);
      setEvents([]);
    } finally {
      setLoading(false);
    }
  }

  function formatDateRange(e) {
    const s = e?.start_datetime || '';
    const ed = e?.end_datetime || '';
    if (!s && !ed) return '-';
    return `${s}${s && ed ? ' — ' : ''}${ed}`;
  }

  const filtered = events.filter(ev => {
    if (!search) return true;
    const s = search.trim().toLowerCase();
    return (
      (ev.name || '').toLowerCase().includes(s) ||
      (ev.description || '').toLowerCase().includes(s) ||
      (ev.location || '').toLowerCase().includes(s)
    );
  });

  return (
    <div className="canvas" style={{ minHeight: 'calc(100vh - 120px)' }}>
      {/* top header row with logo, title, actions */}
      <div className="hero" style={{ marginBottom: 14 }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: 14 }}>
          {/* try site logo first, fallback to INC badge */}
          <Logo />
          <div>
            <div className="hero-title" style={{ fontSize: 20 }}>INC Event Management</div>
            <div className="hero-sub" style={{ fontSize: 13 }}>
              {user ? <>स्वागत — <strong>{user.name || user.id}</strong></> : 'कृपया लॉगिन करें ताकि आप इवेंट जोड़/अपडेट कर सकें।'}
            </div>
          </div>
        </div>

        <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
          {user && user.role === 'admin' && <button className="button" onClick={onAdd}>+ Add Event</button>}
          <button className="button ghost" onClick={() => fetchEvents(type)}>Refresh</button>
          {user ? (
            <button className="button secondary" onClick={onLogout}>Logout</button>
          ) : (
            <a className="button" href="/login">Login</a>
          )}
        </div>
      </div>

      {/* filters / radio buttons and search */}
      <div className="controls-row" style={{ marginBottom: 12 }}>
        <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
          <label style={{ display: 'flex', gap: 6, alignItems: 'center' }}>
            <input
              type="radio"
              name="eventType"
              checked={type === 'ongoing'}
              onChange={() => setType('ongoing')}
            />{' '}
            Ongoing
          </label>
          <label style={{ display: 'flex', gap: 6, alignItems: 'center' }}>
            <input
              type="radio"
              name="eventType"
              checked={type === 'previous'}
              onChange={() => setType('previous')}
            />{' '}
            Previous
          </label>
        </div>

        <div style={{ marginLeft: 'auto', display: 'flex', gap: 8, alignItems: 'center' }}>
          <input
            type="search"
            placeholder="Search name, location, description..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ padding: 8, borderRadius: 8, border: '1px solid rgba(0,0,0,0.08)', minWidth: 220 }}
          />
          <button className="button ghost" onClick={() => setSearch('')}>Clear</button>
        </div>
      </div>

      {/* Table */}
      <div className="table-wrap">
        {loading ? (
          <div className="empty">Loading...</div>
        ) : filtered.length === 0 ? (
          <div className="empty">
            <div style={{ fontSize: 18, fontWeight: 700 }}>No events found</div>
            <div className="muted">Try clearing filters or switch type</div>
          </div>
        ) : (
          <table className="table" role="table" aria-label="Events table">
            <thead>
              <tr>
                <th style={{ width: 70 }}>S. No.</th>
                <th>Event Details (Name)</th>
                <th style={{ width: 240 }}>Event Date</th>
                <th style={{ width: 220 }}>Action</th>
              </tr>
            </thead>
            <tbody>
              {filtered.map((e, idx) => (
                <tr key={e.id || idx}>
                  <td>{idx + 1}</td>
                  <td>
                    <a
                      href="#"
                      onClick={(ev) => { ev.preventDefault(); onOpen && onOpen(e.id); }}
                    >
                      {e.name}
                    </a>
                  </td>
                  <td>{formatDateRange(e)}</td>
                  <td>
                    <div style={{ display: 'flex', gap: 8 }}>
                      <button className="button small" onClick={() => onOpen && onOpen(e.id)}>Show Details</button>

                      {/* If viewing previous events, Update must be disabled (not updatable) */}
                      <button
                        className="button secondary small"
                        onClick={() => onEdit && onEdit(e.id)}
                        disabled={type === 'previous'}
                        title={type === 'previous' ? 'Previous events cannot be updated' : 'Update event'}
                      >
                        Update
                      </button>

                      {user && user.role === 'admin' && (
                        <button className="button small" onClick={() => onReport && onReport(e.id)}>Report</button>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

/* Small Logo component: tries /logo192.png in public, otherwise shows INC badge */
function Logo() {
  // If you prefer a file-based logo, drop an image at public/logo192.png
  // we attempt to load it via an <img> tag. If missing, browser shows broken image;
  // to avoid that, use a CSS fallback or keep the INC badge.
  const logoUrl = '/logo192.png'; // CRA default public logo path
  // We'll render both but hide broken image visually if it fails: use onError to hide.
  const [ok, setOk] = useState(true);
  return ok ? (
    <img
      src={logoUrl}
      alt="INC logo"
      className="site-logo"
      onError={() => setOk(false)}
      style={{ width: 64, height: 64, borderRadius: 12, objectFit: 'cover', boxShadow: 'var(--shadow-sm)' }}
    />
  ) : (
    <div className="logo-badge" style={{ width: 64, height: 64, borderRadius: 12, fontSize: 18 }}>INC</div>
  );
}
