import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from "react-router-dom";
import Home from "./components/Home";
import Login from "./components/Login";
import AddEvent from "./components/AddEvent";
import UpdateEvent from "./components/UpdateEvent";
import EventDetails from "./components/EventDetails";
import "./styles.css";

function App() {
  const [user, setUser] = useState(null);

  const handleLogin = (loggedInUser) => {
    setUser(loggedInUser);
  };

  const handleLogout = () => {
    setUser(null);
  };

  const handleAddEvent = () => {
    alert("Add event clicked!");
  };

  const handleEditEvent = (id) => {
    alert("Edit event with ID: " + id);
  };

  const handleOpenEvent = (id) => {
    alert("Open event details for ID: " + id);
  };

  const handleReport = (id) => {
    alert("Generate report for ID: " + id);
  };

  return (
    <Router>
      <div className="app">
        <header className="header">
          <h1>INC Event Management</h1>
          <nav style={{ marginLeft: "auto" }}>
            <Link className="button" to="/">Home</Link>
            <Link className="button" to="/add">Add Event</Link>
            <Link className="button" to="/login">Login</Link>
          </nav>
        </header>

        <main>
          <Routes>
            <Route
              path="/"
              element={
                <Home
                  user={user}
                  onLogout={handleLogout}
                  onAdd={handleAddEvent}
                  onEdit={handleEditEvent}
                  onOpen={handleOpenEvent}
                  onReport={handleReport}
                />
              }
            />
            <Route path="/login" element={<Login onLogin={handleLogin} />} />
            <Route path="/add" element={<AddEvent />} />
            <Route path="/update/:id" element={<UpdateEvent />} />
            <Route path="/event/:id" element={<EventDetails />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
