// src/components/UpdateEvent.js
import React, { useEffect, useState } from "react";
import { getEvent, updateEvent } from "../api";
import { useNavigate } from "react-router-dom";

export default function UpdateEvent({ id, user, onBack }) {
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  // editable fields
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [start, setStart] = useState("");
  const [end, setEnd] = useState("");
  const [issue, setIssue] = useState("");
  const [level, setLevel] = useState("jila");
  const [eventType, setEventType] = useState("धरणा");
  const [location, setLocation] = useState("");
  const [attendees, setAttendees] = useState(0);

  // media
  const [photos, setPhotos] = useState([]);
  const [photoPreviews, setPhotoPreviews] = useState([]);
  const [media, setMedia] = useState([]);
  const [mediaPreviews, setMediaPreviews] = useState([]);
  const [video, setVideo] = useState(null);
  const [videoError, setVideoError] = useState(null);

  useEffect(() => {
    load();
    // eslint-disable-next-line
  }, []);

  async function load() {
    setLoading(true);
    try {
      const e = await getEvent(id);
      setEvent(e);
      setName(e.name || "");
      setDescription(e.description || "");
      setStart(e.start_datetime || "");
      setEnd(e.end_datetime || "");
      setIssue(e.issue_date || "");
      setLevel(e.level || "jila");
      setEventType(e.event_type || e.eventType || "धरणा");
      setLocation(e.location || "");
      setAttendees(e.attendees_count || 0);
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }

  function fileToDataUrl(file) {
    return new Promise((res) => {
      const fr = new FileReader();
      fr.onload = () => res(fr.result);
      fr.onerror = () => res(null);
      fr.readAsDataURL(file);
    });
  }

  function handleFiles(e, setter, previewSetter, maxFiles = 10) {
    const fl = Array.from(e.target.files || []);
    if (fl.length > maxFiles) {
      alert(`अधिकतम ${maxFiles} फ़ाइलें अनुमति हैं`);
      return;
    }
    setter(fl);
    const imgs = fl.filter((f) => f.type.startsWith("image/"));
    Promise.all(imgs.map(fileToDataUrl)).then((previews) => previewSetter(previews));
  }

  function handleVideoChange(e) {
    setVideoError(null);
    const f = e.target.files[0];
    if (!f) {
      setVideo(null);
      return;
    }
    if (f.size < 10 * 1024 * 1024) {
      setVideoError("वीडियो कम से कम 10MB होना चाहिए");
      setVideo(null);
      return;
    }
    setVideo(f);
  }

  async function submit(e) {
    e.preventDefault();
    if (!user || !user.id) {
      alert("कृपया पहले लॉगिन करें");
      return;
    }
    setSubmitting(true);
    const fd = new FormData();

    // If admin -> allow editing all core fields
    if (user.role === "admin") {
      fd.append("name", name);
      fd.append("description", description);
      fd.append("start_datetime", start);
      fd.append("end_datetime", end);
      fd.append("issue_date", issue);
      fd.append("level", level);
      fd.append("event_type", eventType);
    } else {
      // Non-admin: only allowed to modify location/attendees
      fd.append("userId", user.id);
    }

    fd.append("location", location);
    fd.append("attendees_count", attendees);

    photos.forEach((p) => fd.append("photos", p));
    media.forEach((m) => fd.append("mediaPhotos", m));
    if (video) fd.append("video", video);

    try {
      const res = await updateEvent(id, fd);
      if (res.error) alert(res.error);
      else {
        alert("अपडेट सफल");
        if (onBack) onBack();
        else navigate("/");
      }
    } catch (err) {
      console.error(err);
      alert("अपडेट में त्रुटि");
    } finally {
      setSubmitting(false);
    }
  }

  if (loading || !event) return <div className="canvas">लोड हो रहा है...</div>;

  // Admins see editable full-form; users see read-only fields + editable location & attendees
  return (
    <div className="canvas">
      <button className="button ghost" onClick={() => (onBack ? onBack() : navigate(-1))}>वापस</button>
      <h3>इवेंट अपडेट करें</h3>
      <form onSubmit={submit}>
        <div style={{ marginTop: 8 }}>
          <label>कार्यक्रम का नाम</label>
          {user.role === "admin" ? (
            <input value={name} onChange={(e) => setName(e.target.value)} />
          ) : (
            <div className="muted">{name}</div>
          )}
        </div>

        <div style={{ marginTop: 8 }}>
          <label>विवरण</label>
          {user.role === "admin" ? (
            <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
          ) : (
            <div className="muted">{description}</div>
          )}
        </div>

        <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
          <div style={{ flex: 1 }}>
            <label>शुरू (YYYY-MM-DD HH:MM:SS)</label>
            {user.role === "admin" ? (
              <input value={start} onChange={(e) => setStart(e.target.value)} />
            ) : (
              <div className="muted">{start}</div>
            )}
          </div>
          <div style={{ flex: 1 }}>
            <label>समाप्त (YYYY-MM-DD HH:MM:SS)</label>
            {user.role === "admin" ? (
              <input value={end} onChange={(e) => setEnd(e.target.value)} />
            ) : (
              <div className="muted">{end}</div>
            )}
          </div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>जारी करने की तिथि</label>
          {user.role === "admin" ? (
            <input type="date" value={issue} onChange={(e) => setIssue(e.target.value)} />
          ) : (
            <div className="muted">{issue}</div>
          )}
        </div>

        <div style={{ marginTop: 8 }}>
          <label>कार्यक्रम प्रकार</label>
          {user.role === "admin" ? (
            <select value={eventType} onChange={(e) => setEventType(e.target.value)}>
              <option>धरणा</option>
              <option>मीटिंग</option>
              <option>बंद</option>
              <option>रैली</option>
              <option>सभा</option>
              <option>ग्यापन</option>
            </select>
          ) : (
            <div className="muted">{eventType}</div>
          )}
        </div>

        <div style={{ marginTop: 8 }}>
          <label>स्थान</label>
          <input value={location} onChange={(e) => setLocation(e.target.value)} />
        </div>

        <div style={{ marginTop: 8 }}>
          <label>हाज़िरी की संख्या</label>
          <input type="number" value={attendees} onChange={(e) => setAttendees(Number(e.target.value))} />
        </div>

        <div style={{ marginTop: 8 }}>
          <label>Photos (अधिकतम 10)</label>
          <input type="file" accept="image/*" multiple onChange={(e) => handleFiles(e, setPhotos, setPhotoPreviews, 10)} />
          <div style={{ display: "flex", gap: 8, marginTop: 8 }}>{photoPreviews.map((p, i) => <img key={i} src={p} alt={`p${i}`} style={{ height: 80, borderRadius: 8 }} />)}</div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>मीडिया कवरेज फ़ोटो (अधिकतम 5)</label>
          <input type="file" accept="image/*" multiple onChange={(e) => handleFiles(e, setMedia, setMediaPreviews, 5)} />
          <div style={{ display: "flex", gap: 8, marginTop: 8 }}>{mediaPreviews.map((p, i) => <img key={i} src={p} alt={`m${i}`} style={{ height: 80, borderRadius: 8 }} />)}</div>
        </div>

        <div style={{ marginTop: 8 }}>
          <label>वीडियो (कम से कम 10MB)</label>
          <input type="file" accept="video/*" onChange={handleVideoChange} />
          {videoError && <div style={{ color: "red" }}>{videoError}</div>}
        </div>

        <div style={{ marginTop: 12, display: "flex", gap: 8 }}>
          <button className="button" type="submit" disabled={submitting}>{submitting ? "सबमिट हो रहा है..." : "अपडेट भेजें"}</button>
          <button className="button ghost" type="button" onClick={() => (onBack ? onBack() : navigate(-1))}>रद्द करें</button>
        </div>
      </form>
    </div>
  );
}
