// src/App.js
import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
  useParams,
} from "react-router-dom";

import Home from "./components/Home";
import Login from "./components/Login";
import AddEvent from "./components/AddEvent";
import UpdateEvent from "./components/UpdateEvent";
import EventDetails from "./components/EventDetails";
import "./styles.css";

/*
  Navbar items (visible exactly as requested):
  - होम (Home)
  - इवेंट जोड़ें (Add Event)
  - इवेंट विवरण (Show Event Details Page)
  - अपडेट पेज (Update Page)
  - लॉगिन पेज (Login Page)
*/

function AppShellInner() {
  // load persisted user (if any)
  const [user, setUser] = useState(() => {
    try {
      return JSON.parse(localStorage.getItem("inc_user") || "null");
    } catch {
      return null;
    }
  });

  const navigate = useNavigate();

  useEffect(() => {
    if (user) localStorage.setItem("inc_user", JSON.stringify(user));
    else localStorage.removeItem("inc_user");
  }, [user]);

  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem("inc_user");
    navigate("/");
  };

  // wrappers to pass props into component routes
  function HomeWrapper() {
    return (
      <Home
        user={user}
        onAdd={() => navigate("/add")}
        onEdit={(id) => navigate(`/update/${id}`)}
        onOpen={(id) => navigate(`/event/${id}`)}
        onReport={(id) => navigate(`/event/${id}/report`)}
        onLogout={handleLogout}
      />
    );
  }

  function LoginWrapper() {
    const onLogin = (u) => {
      setUser(u);
      navigate("/");
    };
    return <Login onLogin={onLogin} />;
  }

  function AddWrapper() {
    const onBack = () => navigate(-1);
    return <AddEvent user={user} onBack={onBack} />;
  }

  function UpdateWrapper() {
    const { id } = useParams();
    const onBack = () => navigate(-1);
    return <UpdateEvent id={id} user={user} onBack={onBack} />;
  }

  function EventWrapper() {
    const { id } = useParams();
    const onBack = () => navigate(-1);
    const onEdit = (eid) => navigate(`/update/${eid}`);
    return <EventDetails id={id} user={user} onBack={onBack} onEdit={onEdit} />;
  }

  return (
    <>
      <header className="header">
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <Link to="/">
            <img
              src="/logo192.png"
              alt="logo"
              style={{ width: 44, height: 44, borderRadius: 8 }}
              onError={(e) => (e.target.style.display = "none")}
            />
          </Link>
          <h1>INC Event Management</h1>
        </div>

        <nav style={{ marginLeft: "auto", display: "flex", gap: 10, alignItems: "center" }}>
          {/* 1. Home */}
          <Link className="button" to="/">
            होम
          </Link>

          {/* 2. Add Event */}
          <Link className="button" to="/add">
            इवेंट जोड़ें
          </Link>

          {/* 3. Show Event Details Page -> goes to a small helper page where user types ID */}
          <Link className="button" to="/show-event">
            इवेंट विवरण
          </Link>

          {/* 4. Update Page -> helper page to enter ID and navigate to /update/:id */}
          <Link className="button" to="/update-page">
            अपडेट पेज
          </Link>

          {/* 5. Login Page */}
          <Link className="button" to="/login">
            लॉगिन पेज
          </Link>

          {/* Show logout if logged in */}
          {user ? (
            <button className="button ghost" onClick={handleLogout}>
              लॉग आउट
            </button>
          ) : null}
        </nav>
      </header>

      <main>
        <div style={{ maxWidth: 1200, margin: "20px auto", padding: 12 }}>
          <Routes>
            <Route path="/" element={<HomeWrapper />} />
            <Route path="/login" element={<LoginWrapper />} />
            <Route path="/add" element={<AddWrapper />} />
            <Route path="/update/:id" element={<UpdateWrapper />} />
            <Route path="/event/:id" element={<EventWrapper />} />

            {/* Helper pages for the navbar items that require an ID */}
            <Route path="/show-event" element={<ShowEventPage />} />
            <Route path="/update-page" element={<UpdatePage />} />
          </Routes>
        </div>
      </main>
    </>
  );
}

/* Helper page: ask for Event ID then navigate to /event/:id */
function ShowEventPage() {
  const [eid, setEid] = useState("");
  const navigate = useNavigate();

  function go() {
    const id = (eid || "").trim();
    if (!id) {
      alert("कृपया Event ID डालें");
      return;
    }
    navigate(`/event/${id}`);
  }

  return (
    <div className="canvas" style={{ minHeight: "60vh" }}>
      <h2>इवेंट विवरण देखें</h2>
      <p className="muted">कृपया इवेंट का ID दर्ज करें और 'देखें' पर क्लिक करें</p>
      <div style={{ display: "flex", gap: 8, marginTop: 12, maxWidth: 420 }}>
        <input
          placeholder="Event ID"
          value={eid}
          onChange={(e) => setEid(e.target.value)}
          style={{ padding: 10, borderRadius: 8, border: "1px solid #ddd", flex: 1 }}
        />
        <button className="button" onClick={go}>
          देखें
        </button>
      </div>
    </div>
  );
}

/* Helper page: ask for Event ID then navigate to /update/:id */
function UpdatePage() {
  const [eid, setEid] = useState("");
  const navigate = useNavigate();

  function go() {
    const id = (eid || "").trim();
    if (!id) {
      alert("कृपया Event ID डालें");
      return;
    }
    navigate(`/update/${id}`);
  }

  return (
    <div className="canvas" style={{ minHeight: "60vh" }}>
      <h2>इवेंट अपडेट पेज</h2>
      <p className="muted">कृपया वह Event ID दर्ज करें जिसे आप अपडेट करना चाहते हैं</p>
      <div style={{ display: "flex", gap: 8, marginTop: 12, maxWidth: 420 }}>
        <input
          placeholder="Event ID"
          value={eid}
          onChange={(e) => setEid(e.target.value)}
          style={{ padding: 10, borderRadius: 8, border: "1px solid #ddd", flex: 1 }}
        />
        <button className="button secondary" onClick={go}>
          अपडेट पेज खोलें
        </button>
      </div>
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <AppShellInner />
    </Router>
  );
}
