import React, { useEffect, useState } from "react";
import { getAllEvents, deleteEvent } from "../api";
import { Link } from "react-router-dom";

export default function Home() {
  const [events, setEvents] = useState([]);
  const [message, setMessage] = useState("");

  const fetchEvents = async () => {
    try {
      const res = await getAllEvents();
      setEvents(res || []);
    } catch (err) {
      setMessage("त्रुटि: सर्वर से डेटा प्राप्त नहीं हो सका।");
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("क्या आप सच में इस कार्यक्रम को हटाना चाहते हैं?")) return;
    try {
      const res = await deleteEvent(id);
      if (res.success) {
        setEvents(events.filter((e) => e.id !== id));
        setMessage("कार्यक्रम सफलतापूर्वक हटाया गया।");
      } else {
        setMessage("त्रुटि: कार्यक्रम नहीं हटाया जा सका।");
      }
    } catch (err) {
      setMessage("त्रुटि: सर्वर समस्या।");
    }
  };

  useEffect(() => {
    fetchEvents();
  }, []);

  return (
    <div className="app">
      <main>
        <h2>सभी कार्यक्रम</h2>
        {message && <div className="notice">{message}</div>}

        <div className="event-list">
          {events.map((event) => (
            <div className="event-card" key={event.id}>
              <h3>{event.name}</h3>
              <p>{event.location}</p>
              <p>दिनांक: {new Date(event.startDate).toLocaleDateString()}</p>
              <div className="actions">
                <Link to={`/event/${event.id}`} className="button">विवरण</Link>
                <Link to={`/update/${event.id}`} className="button">संपादित करें</Link>
                <button onClick={() => handleDelete(event.id)} className="button delete">हटाएँ</button>
              </div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
