// src/components/AddEvent.js
import React, { useState } from "react";
import { addEvent } from "../api";
import { useNavigate } from "react-router-dom";

export default function AddEvent({ user, onBack }) {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: "",
    description: "",
    startDate: "",
    endDate: "",
    issueDate: "",
    eventType: "मीटिंग",
    location: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handlePhotoChange = (e) => {
    const files = Array.from(e.target.files || []);
    if (photos.length + files.length > 10) { setMessage("आप अधिकतम 10 फ़ोटो ही चुन सकते हैं।"); return; }
    setPhotos((p)=>[...p,...files]);
  };
  const handleRemovePhoto = (i) => setPhotos((p)=>p.filter((_,idx)=>idx!==i));

  const handleMediaPhotoChange = (e) => {
    const files = Array.from(e.target.files || []);
    if (mediaPhotos.length + files.length > 5) { setMessage("आप अधिकतम 5 मीडिया फ़ोटो ही चुन सकते हैं।"); return; }
    setMediaPhotos((p)=>[...p,...files]);
  };
  const handleRemoveMediaPhoto = (i) => setMediaPhotos((p)=>p.filter((_,idx)=>idx!==i));

  const handleVideoChange = (e) => {
    setMessage("");
    const f = e.target.files[0];
    if (!f) return;
    const sizeMB = f.size / (1024*1024);
    if (sizeMB < 10) { setMessage("वीडियो कम से कम 10MB का होना चाहिए।"); e.target.value=""; return; }
    if (sizeMB > 200) { setMessage("वीडियो 200MB से अधिक नहीं होना चाहिए।"); e.target.value=""; return; }
    setVideo(f);
  };

  function datetimeLocalToSQL(v) { if (!v) return ""; return v.replace("T"," ") + ":00"; }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(""); setLoading(true);
    if (!form.name || !form.description || !form.startDate || !form.endDate || !form.issueDate || !form.location) {
      setMessage("कृपया सभी आवश्यक फ़ील्ड भरें।");
      setLoading(false);
      return;
    }

    try {
      const fd = new FormData();
      fd.append("name", form.name);
      fd.append("description", form.description);
      fd.append("start_datetime", datetimeLocalToSQL(form.startDate));
      fd.append("end_datetime", datetimeLocalToSQL(form.endDate));
      fd.append("issue_date", form.issueDate);
      fd.append("location", form.location);
      fd.append("event_type", form.eventType);
      if (user && user.id) fd.append("created_by", user.id);

      photos.forEach((f)=>fd.append("photos", f));
      mediaPhotos.forEach((f)=>fd.append("mediaPhotos", f));
      if (video) fd.append("video", video);

      const res = await addEvent(fd);
      if (res.error) {
        setMessage("त्रुटि: " + res.error);
      } else {
        setMessage("कार्यक्रम सफलतापूर्वक जोड़ा गया।");
        if (res.id) navigate(`/event/${res.id}`);
        else {
          setForm({ name:"", description:"", startDate:"", endDate:"", issueDate:"", eventType:"मीटिंग", location:"" });
          setPhotos([]); setMediaPhotos([]); setVideo(null);
        }
      }
    } catch (err) {
      setMessage("त्रुटि: सर्वर से संपर्क नहीं हो सका।");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app">
      <main>
        <h2>नया कार्यक्रम जोड़ें</h2>
        {message && <div className="notice">{message}</div>}

        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div><label>कार्यक्रम का नाम</label><input type="text" name="name" value={form.name} onChange={handleChange} required /></div>
          <div><label>विवरण</label><textarea name="description" value={form.description} onChange={handleChange} required /></div>

          <div className="row">
            <div className="col"><label>प्रारंभ दिनांक / समय</label><input type="datetime-local" name="startDate" value={form.startDate} onChange={handleChange} required /></div>
            <div className="col"><label>समापन दिनांक / समय</label><input type="datetime-local" name="endDate" value={form.endDate} onChange={handleChange} required /></div>
          </div>

          <div><label>जारी करने की दिनांक</label><input type="date" name="issueDate" value={form.issueDate} onChange={handleChange} required /></div>

          <div><label>स्थान</label><input type="text" name="location" value={form.location} onChange={handleChange} required /></div>

          <div><label>कार्यक्रम प्रकार</label>
            <select name="eventType" value={form.eventType} onChange={handleChange}>
              <option>धरणा</option><option>मीटिंग</option><option>बंद</option><option>रैली</option><option>सभा</option><option>ग्यापन</option>
            </select>
          </div>

          <div>
            <label>फ़ोटो (अधिकतम 10)</label>
            <input type="file" multiple accept="image/*" onChange={handlePhotoChange} />
            <div className="event-thumbs row" style={{ marginTop:8 }}>
              {photos.map((p,i)=>(
                <div key={i} style={{ position:"relative" }}>
                  <img src={URL.createObjectURL(p)} alt="preview" height="70" style={{ marginRight:8 }} />
                  <button type="button" onClick={()=>handleRemovePhoto(i)} style={{ position:"absolute", top:0, right:0, background:"red", color:"white", border:"none", borderRadius:"50%", width:"20px", height:"20px" }}>×</button>
                </div>
              ))}
            </div>
          </div>

          <div>
            <label>मीडिया कवरेज फ़ोटो (अधिकतम 5)</label>
            <input type="file" multiple accept="image/*" onChange={handleMediaPhotoChange} />
            <div className="event-thumbs row" style={{ marginTop:8 }}>
              {mediaPhotos.map((p,i)=>(
                <div key={i} style={{ position:"relative" }}>
                  <img src={URL.createObjectURL(p)} alt="media" height="70" style={{ marginRight:8 }} />
                  <button type="button" onClick={()=>handleRemoveMediaPhoto(i)} style={{ position:"absolute", top:0, right:0, background:"red", color:"white", border:"none", borderRadius:"50%", width:"20px", height:"20px" }}>×</button>
                </div>
              ))}
            </div>
          </div>

          <div>
            <label>वीडियो (कम से कम 10MB, अधिकतम 200MB)</label>
            <input type="file" accept="video/*" onChange={handleVideoChange} />
            {video && <video controls src={URL.createObjectURL(video)} width="200" style={{ marginTop:10 }} />}
          </div>

          <div className="form-actions">
            <button className="button" disabled={loading}>{loading ? "जोड़ा जा रहा है..." : "कार्यक्रम जोड़ें"}</button>
          </div>
        </form>
      </main>
    </div>
  );
}
