import React, { useState, useEffect, useMemo } from 'react';
import { listEvents } from '../api';

/*
  Props:
    - onOpen(id)
    - user
    - onAdd()
    - onEdit(id)
    - onReport(id)
    - onLogout()
*/

export default function Home({ onOpen, user, onAdd, onEdit, onReport, onLogout }) {
  const [type, setType] = useState('ongoing'); // ongoing | previous
  const [events, setEvents] = useState([]);
  const [search, setSearch] = useState('');
  const [loading, setLoading] = useState(false);

  // pagination
  const [page, setPage] = useState(1);
  const PAGE_SIZE = 8;

  // stats
  const [countOngoing, setCountOngoing] = useState(null);
  const [countPrevious, setCountPrevious] = useState(null);

  // fetch events for the current type (and also update stats lazily)
  useEffect(() => {
    fetchEvents(type);
    // fetch counts separately (small extra requests) ‚Äî only if not loaded
    if (countOngoing === null || countPrevious === null) fetchCounts();
    // reset page when type/search changes
    setPage(1);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [type]);

  async function fetchEvents(t) {
    setLoading(true);
    try {
      const r = await listEvents(t);
      setEvents(Array.isArray(r) ? r : []);
    } catch (err) {
      console.error('Error loading events:', err);
      setEvents([]);
    } finally {
      setLoading(false);
    }
  }

  async function fetchCounts() {
    try {
      const o = await listEvents('ongoing');
      const p = await listEvents('previous');
      setCountOngoing(Array.isArray(o) ? o.length : 0);
      setCountPrevious(Array.isArray(p) ? p.length : 0);
    } catch (err) {
      console.error('failed to fetch counts', err);
      setCountOngoing(0);
      setCountPrevious(0);
    }
  }

  // filtered events by search
  const filtered = useMemo(() => {
    if (!search) return events;
    const s = search.trim().toLowerCase();
    return events.filter(ev => {
      return (ev.name || '').toLowerCase().includes(s) ||
             (ev.description || '').toLowerCase().includes(s) ||
             (ev.location || '').toLowerCase().includes(s);
    });
  }, [events, search]);

  // pagination slice
  const totalPages = Math.max(1, Math.ceil(filtered.length / PAGE_SIZE));
  const paged = filtered.slice((page - 1) * PAGE_SIZE, page * PAGE_SIZE);

  // UI helpers
  function formatDateRange(ev) {
    if (!ev) return '';
    const s = ev.start_datetime || '';
    const e = ev.end_datetime || '';
    return `${s}${s && e ? ' ‚Äî ' : ''}${e}`;
  }

  // Render
  return (
    <div className="canvas">
      <div className="hero">
        <div className="hero-left">
          <div className="logo-badge">INC</div>
          <div>
            <div className="hero-title">‡§á‡§Ç‡§°‡§ø‡§Ø‡§® ‡§®‡•á‡§∂‡§®‡§≤ ‡§ï‡§æ‡§Ç‡§ó‡•ç‡§∞‡•á‡§∏ ‚Äî ‡§á‡§µ‡•á‡§Ç‡§ü‡•ç‡§∏</div>
            <div className="hero-sub">
              {user ? <>‡§∏‡•ç‡§µ‡§æ‡§ó‡§§, <strong>{user.name || user.id}</strong> ‚Äî ‡§Ø‡§π‡§æ‡§Å ‡§Ü‡§™‡§ï‡•á ‡§á‡§µ‡•á‡§Ç‡§ü‡•ç‡§∏ ‡§π‡•à‡§Ç</> : '‡§ï‡•É‡§™‡§Ø‡§æ ‡§≤‡•â‡§ó‡§ø‡§® ‡§ï‡§∞‡•á‡§Ç ‡§§‡§æ‡§ï‡§ø ‡§Ü‡§™ ‡§á‡§µ‡•á‡§Ç‡§ü ‡§ú‡•ã‡§°‡§º/‡§Ö‡§™‡§°‡•á‡§ü ‡§ï‡§∞ ‡§∏‡§ï‡•á‡§Ç‡•§'}
            </div>
          </div>
        </div>

        <div style={{ display: 'flex', gap: 10, alignItems: 'center', flexWrap: 'wrap' }}>
          <div style={{ display: 'flex', gap: 8 }}>
            {user && user.role === 'admin' && (
              <button className="button" onClick={onAdd}>+ ‡§®‡§Ø‡§æ ‡§á‡§µ‡•á‡§Ç‡§ü</button>
            )}
            <button className="button ghost" onClick={() => window.location.reload()}>‡§∞‡§ø‡§´‡•ç‡§∞‡•á‡§∂</button>
          </div>
          {user ? (
            <button className="button secondary" onClick={onLogout}>‡§≤‡•â‡§ó ‡§Ü‡§â‡§ü</button>
          ) : (
            <div style={{ color: 'var(--muted)', fontSize: 13 }}>‡§≤‡•â‡§ó‡§ø‡§® ‡§Ü‡§µ‡§∂‡•ç‡§Ø‡§ï</div>
          )}
        </div>
      </div>

      <div className="stats">
        <div className="stat">
          <div style={{flex:1}}>
            <div className="num">{countOngoing === null ? '‚Äî' : countOngoing}</div>
            <div className="label">‡§ö‡§≤ ‡§∞‡§π‡•á ‡§á‡§µ‡•á‡§Ç‡§ü</div>
          </div>
          <div style={{alignSelf:'center'}}>
            <div style={{fontSize:20, color:'var(--primary)', fontWeight:700}}>{/* icon placeholder */}üìÖ</div>
          </div>
        </div>

        <div className="stat">
          <div style={{flex:1}}>
            <div className="num">{countPrevious === null ? '‚Äî' : countPrevious}</div>
            <div className="label">‡§™‡§ø‡§õ‡§≤‡•á ‡§á‡§µ‡•á‡§Ç‡§ü</div>
          </div>
          <div style={{alignSelf:'center'}}>
            <div style={{fontSize:20, color:'var(--accent)', fontWeight:700}}>üéûÔ∏è</div>
          </div>
        </div>

        <div className="stat">
          <div style={{flex:1}}>
            <div className="num">{events.length}</div>
            <div className="label">‡§¶‡§ø‡§ñ‡§æ‡§è ‡§ú‡§æ ‡§∞‡§π‡•á</div>
          </div>
          <div style={{alignSelf:'center'}}>
            <div style={{fontSize:20, color:'#f59e0b', fontWeight:700}}>üìã</div>
          </div>
        </div>
      </div>

      <div className="controls-row">
        <div className="search">
          <input
            type="search"
            placeholder="‡§ñ‡•ã‡§ú‡•á‡§Ç ‡§®‡§æ‡§Æ, ‡§∏‡•ç‡§•‡§æ‡§® ‡§Ø‡§æ ‡§µ‡§ø‡§µ‡§∞‡§£..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
          />
          <div style={{display:'flex', gap:8}}>
            <button className={`button ${search ? '' : 'secondary'}`} onClick={() => setSearch('')}>‡§∏‡§æ‡§´‡§º ‡§ï‡§∞‡•á‡§Ç</button>
          </div>
        </div>

        <div className="filter-controls">
          <label style={{display:'flex', gap:6, alignItems:'center', fontWeight:700}}>
            <input type="radio" name="etype" checked={type === 'ongoing'} onChange={() => setType('ongoing')} /> ‡§ö‡§≤ ‡§∞‡§π‡•Ä
          </label>
          <label style={{display:'flex', gap:6, alignItems:'center', fontWeight:700}}>
            <input type="radio" name="etype" checked={type === 'previous'} onChange={() => setType('previous')} /> ‡§™‡§ø‡§õ‡§≤‡•á
          </label>
        </div>
      </div>

      {/* Events: show grid on wider screens, fallback table for accessibility */}
      {loading ? (
        <div className="empty">‡§≤‡•ã‡§° ‡§π‡•ã ‡§∞‡§π‡§æ ‡§π‡•à‚Ä¶</div>
      ) : filtered.length === 0 ? (
        <div className="empty">
          <div style={{fontSize:18, fontWeight:700}}>‡§ï‡•ã‡§à ‡§á‡§µ‡•á‡§Ç‡§ü ‡§®‡§π‡•Ä‡§Ç ‡§Æ‡§ø‡§≤‡§æ</div>
          <div className="muted">‡§µ‡•à‡§ï‡§≤‡•ç‡§™‡§ø‡§ï: ‡§´‡§º‡§ø‡§≤‡•ç‡§ü‡§∞ ‡§∏‡§æ‡§´‡§º ‡§ï‡§∞‡•á‡§Ç ‡§Ø‡§æ ‡§®‡§Ø‡§æ ‡§á‡§µ‡•á‡§Ç‡§ü ‡§ú‡•ã‡§°‡§º‡•á‡§Ç</div>
        </div>
      ) : (
        <>
          {/* Grid of cards */}
          <div className="events-grid" style={{marginTop:12}}>
            {paged.map((e, i) => {
              // obtain a thumbnail if available
              let thumb = null;
              try {
                const photos = Array.isArray(e.photos) ? e.photos : (typeof e.photos === 'string' ? JSON.parse(e.photos || '[]') : []);
                if (photos && photos.length) thumb = photos[0];
              } catch (err) { /* ignore parse errors */ }

              return (
                <div className="event-card" key={e.id || i}>
                  <div className="event-top">
                    <div className="thumb">
                      {thumb ? <img src={thumb} alt={e.name} /> : <div style={{display:'flex',alignItems:'center',justifyContent:'center',height:'100%',color:'var(--muted)'}}>No Image</div>}
                    </div>
                    <div className="event-meta">
                      <div className="event-title">{e.name}</div>
                      <div className="event-sub">{formatDateRange(e)} ‚Ä¢ {e.location}</div>
                      <div style={{marginTop:8}} className="badges">
                        <div className="badge">{e.level || '‡§∏‡•ç‡§§‡§∞ N/A'}</div>
                        <div className="badge">{e.event_type || '‡§™‡•ç‡§∞‡§ï‡§æ‡§∞ N/A'}</div>
                      </div>
                    </div>
                  </div>

                  <div style={{color:'var(--muted)', fontSize:13, minHeight:42}}>
                    {e.description ? (e.description.length > 120 ? e.description.slice(0,120)+'‚Ä¶' : e.description) : <span className="muted">‡§µ‡§ø‡§µ‡§∞‡§£ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§®‡§π‡•Ä‡§Ç</span>}
                  </div>

                  <div className="card-actions">
                    <button className="button" onClick={() => onOpen(e.id)}>‡§°‡§ø‡§ü‡•á‡§≤‡•ç‡§∏</button>
                    <div style={{marginLeft:'auto', display:'flex', gap:8}}>
                      <button className="button secondary" onClick={() => onEdit(e.id)}>‡§Ö‡§™‡§°‡•á‡§ü</button>
                      {user && user.role === 'admin' && <button className="button" onClick={() => onReport(e.id)}>‡§∞‡§ø‡§™‡•ã‡§∞‡•ç‡§ü</button>}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>

          {/* Pagination */}
          <div className="pagination" style={{marginTop:18}}>
            <button className="page-btn" onClick={() => setPage(p => Math.max(1, p-1))} disabled={page===1}>Prev</button>
            {Array.from({length: totalPages}).map((_, idx) => {
              const p = idx + 1;
              return (
                <button
                  key={p}
                  className={`page-btn ${p === page ? 'active' : ''}`}
                  onClick={() => setPage(p)}
                >
                  {p}
                </button>
              );
            })}
            <button className="page-btn" onClick={() => setPage(p => Math.min(totalPages, p+1))} disabled={page===totalPages}>Next</button>
          </div>
        </>
      )}

      {/* Desktop-friendly table view (optional) */}
      <div className="table-wrap" style={{marginTop:18}}>
        <table className="table" aria-label="Event table">
          <thead>
            <tr>
              <th>#</th>
              <th>‡§®‡§æ‡§Æ</th>
              <th>‡§§‡§ø‡§•‡§ø</th>
              <th>‡§∏‡•ç‡§•‡§æ‡§®</th>
              <th>‡§ï‡§æ‡§∞‡•ç‡§Ø</th>
            </tr>
          </thead>
          <tbody>
            {filtered.slice(0, 12).map((e,i) => (
              <tr key={e.id || i}>
                <td>{i+1}</td>
                <td><a href="#" onClick={(ev)=>{ev.preventDefault(); onOpen(e.id);}}>{e.name}</a></td>
                <td>{formatDateRange(e)}</td>
                <td>{e.location}</td>
                <td>
                  <div style={{display:'flex', gap:8}}>
                    <button className="button small" onClick={() => onOpen(e.id)}>‡§¶‡•á‡§ñ‡•á‡§Ç</button>
                    <button className="button secondary small" onClick={() => onEdit(e.id)}>‡§Ö‡§™‡§°‡•á‡§ü</button>
                    {user && user.role === 'admin' && <button className="button small" onClick={() => onReport(e.id)}>‡§∞‡§ø‡§™‡•ã‡§∞‡•ç‡§ü</button>}
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  // helper inside component
  function formatDateRange(ev) {
    if (!ev) return '';
    const s = ev.start_datetime || '';
    const e = ev.end_datetime || '';
    if (!s && !e) return '-';
    return `${s}${s && e ? ' ‚Äî ' : ''}${e}`;
  }
}
