import React, { useState } from "react";
import { login } from "../api";
import { useNavigate } from "react-router-dom";

export default function Login({ onLogin }) {
  const [code, setCode] = useState("");
  const [err, setErr] = useState(null);
  const navigate = useNavigate();

  const doLogin = async () => {
    setErr(null);
    if (code.trim().length !== 4) {
      setErr("कृपया 4 अंकों का कोड दर्ज करें।");
      return;
    }
    try {
      const r = await login(code.trim());
      if (r.error) {
        setErr(r.error);
      } else if (r.user) {
        // keep user in parent via onLogin
        onLogin(r.user);
        // persist user locally
        localStorage.setItem("inc_user", JSON.stringify(r.user));
        navigate("/");
      } else {
        setErr("लॉगिन विफल — सर्वर ने कोई उपयोगकर्ता लौटाया नहीं।");
      }
    } catch (errr) {
      setErr("नेटवर्क त्रुटि। कृपया पुनः प्रयास करें।");
      console.error(errr);
    }
  };

  return (
    <div style={{ maxWidth: 420, margin: "60px auto", background: "white", padding: 20, borderRadius: 8, boxShadow: "var(--shadow-sm)" }}>
      <h2>लॉगिन</h2>
      <p className="muted">कृपया अपना 4-अंकीय कोड दर्ज करें</p>
      <input value={code} onChange={(e) => setCode(e.target.value.replace(/\D/g, ""))} placeholder="उदा. 1111" style={{ padding: 10, width: "100%", marginTop: 8, borderRadius: 8, border: "1px solid #ddd" }} />
      {err && <div style={{ color: "red", marginTop: 8 }}>{err}</div>}
      <div style={{ marginTop: 12, display: "flex", gap: 8 }}>
        <button className="button" onClick={doLogin}>लॉगिन</button>
        <button className="button ghost" onClick={() => { setCode("1111"); }}>Demo: 1111</button>
      </div>
      <div style={{ marginTop: 10, color: "var(--muted)" }}><small>डे़मो कोड: 1111 (admin), 2222 (user)</small></div>
    </div>
  );
}
