// src/components/AddEvent.js
import React, { useState } from "react";
import { addEvent } from "../api";

const AddEvent = ({ onAdded }) => {
  const [form, setForm] = useState({
    name: "",
    description: "",
    start_datetime: "",
    end_datetime: "",
    issue_date: "",
    location: "",
    level: "jila",
    event_type: "",
    attendees_count: "",
  });

  const [photos, setPhotos] = useState([]);
  const [mediaPhotos, setMediaPhotos] = useState([]);
  const [video, setVideo] = useState(null);
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm({ ...form, [name]: value });
  };

  const handlePhotosChange = (e) => {
    setPhotos([...e.target.files]);
  };

  const handleMediaPhotosChange = (e) => {
    setMediaPhotos([...e.target.files]);
  };

  const handleVideoChange = (e) => {
    setVideo(e.target.files[0]);
  };

  const removePhoto = (index) => {
    setPhotos((prev) => prev.filter((_, i) => i !== index));
  };

  const removeMediaPhoto = (index) => {
    setMediaPhotos((prev) => prev.filter((_, i) => i !== index));
  };

  const removeVideo = () => {
    setVideo(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");

    const data = new FormData();
    Object.entries(form).forEach(([key, value]) => data.append(key, value));

    photos.forEach((p) => data.append("photos", p));
    mediaPhotos.forEach((p) => data.append("mediaPhotos", p));
    if (video) data.append("video", video);

    try {
      await addEvent(data);
      setMsg("‚úÖ ‡§á‡§µ‡•á‡§Ç‡§ü ‡§∏‡§´‡§≤‡§§‡§æ‡§™‡•Ç‡§∞‡•ç‡§µ‡§ï ‡§ú‡•ã‡§°‡§º‡§æ ‡§ó‡§Ø‡§æ");
      setForm({
        name: "",
        description: "",
        start_datetime: "",
        end_datetime: "",
        issue_date: "",
        location: "",
        level: "jila",
        event_type: "",
        attendees_count: "",
      });
      setPhotos([]);
      setMediaPhotos([]);
      setVideo(null);
      if (onAdded) onAdded();
    } catch (err) {
      setMsg("‚ùå ‡§§‡•ç‡§∞‡•Å‡§ü‡§ø: " + (err?.response?.data?.error || "‡§∏‡§∞‡•ç‡§µ‡§∞ ‡§∏‡§Æ‡§∏‡•ç‡§Ø‡§æ"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-event-container max-w-3xl mx-auto bg-white p-6 rounded-xl shadow">
      <h2 className="text-xl font-semibold mb-4 text-center">üóìÔ∏è ‡§á‡§µ‡•á‡§Ç‡§ü ‡§ú‡•ã‡§°‡§º‡•á‡§Ç</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <div>
          <label>‡§á‡§µ‡•á‡§Ç‡§ü ‡§ï‡§æ ‡§®‡§æ‡§Æ</label>
          <input name="name" value={form.name} onChange={handleChange} className="input" required />
        </div>

        <div>
          <label>‡§µ‡§ø‡§µ‡§∞‡§£</label>
          <textarea name="description" value={form.description} onChange={handleChange} className="input" rows="3" />
        </div>

        <div>
          <label>‡§™‡•ç‡§∞‡§æ‡§∞‡§Ç‡§≠ ‡§¶‡§ø‡§®‡§æ‡§Ç‡§ï / ‡§∏‡§Æ‡§Ø</label>
          <input
            type="datetime-local"
            name="start_datetime"
            value={form.start_datetime}
            onChange={handleChange}
            className="input"
          />
        </div>

        <div>
          <label>‡§∏‡§Æ‡§æ‡§™‡§® ‡§¶‡§ø‡§®‡§æ‡§Ç‡§ï / ‡§∏‡§Æ‡§Ø</label>
          <input
            type="datetime-local"
            name="end_datetime"
            value={form.end_datetime}
            onChange={handleChange}
            className="input"
          />
        </div>

        <div>
          <label>‡§ú‡§æ‡§∞‡•Ä ‡§ï‡§∞‡§®‡•á ‡§ï‡•Ä ‡§¶‡§ø‡§®‡§æ‡§Ç‡§ï</label>
          <input type="date" name="issue_date" value={form.issue_date} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§∏‡•ç‡§•‡§æ‡§®</label>
          <input name="location" value={form.location} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§∏‡•ç‡§§‡§∞</label>
          <select name="level" value={form.level} onChange={handleChange} className="input">
            <option value="jila">‡§ú‡§ø‡§≤‡§æ</option>
            <option value="block">‡§¨‡•ç‡§≤‡•â‡§ï</option>
          </select>
        </div>

        <div>
          <label>‡§ï‡§æ‡§∞‡•ç‡§Ø‡§ï‡•ç‡§∞‡§Æ ‡§™‡•ç‡§∞‡§ï‡§æ‡§∞</label>
          <input name="event_type" value={form.event_type} onChange={handleChange} className="input" />
        </div>

        <div>
          <label>‡§π‡§æ‡§ú‡§º‡§ø‡§∞‡•Ä ‡§ï‡•Ä ‡§∏‡§Ç‡§ñ‡•ç‡§Ø‡§æ</label>
          <input
            type="number"
            name="attendees_count"
            value={form.attendees_count}
            onChange={handleChange}
            className="input"
            min="0"
          />
        </div>

        <div>
          <label>Photos (‡§Ö‡§ß‡§ø‡§ï‡§§‡§Æ 10)</label>
          <input type="file" multiple accept="image/*" onChange={handlePhotosChange} />
          <div className="flex flex-wrap gap-2 mt-2">
            {photos.map((p, i) => (
              <div key={i} className="relative">
                <img src={URL.createObjectURL(p)} alt="" className="w-20 h-20 object-cover rounded" />
