import React, { useEffect, useState } from "react";
import { getEventById } from "../api";
import { useParams, Link } from "react-router-dom";

export default function EventDetails() {
  const { id } = useParams();
  const [event, setEvent] = useState(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const fetchEvent = async () => {
      try {
        const res = await getEventById(id);
        setEvent(res);
      } catch (err) {
        setMessage("त्रुटि: सर्वर से कार्यक्रम नहीं मिला।");
      }
    };
    fetchEvent();
  }, [id]);

  if (!event) return <p>{message || "लोड हो रहा है..."}</p>;

  return (
    <div className="app">
      <main>
        <h2>{event.name}</h2>
        <p>{event.description}</p>
        <p>स्थान: {event.location}</p>
        <p>प्रकार: {event.eventType}</p>
        <p>जारी दिनांक: {new Date(event.issueDate).toLocaleDateString()}</p>
        <p>प्रारंभ: {new Date(event.startDate).toLocaleString()}</p>
        <p>समापन: {new Date(event.endDate).toLocaleString()}</p>

        {event.photos && event.photos.length > 0 && (
          <>
            <h3>फ़ोटो</h3>
            <div className="event-thumbs row">
              {event.photos.map((p, i) => (
                <img key={i} src={p} alt="event" height="80" />
              ))}
            </div>
          </>
        )}

        {event.mediaPhotos && event.mediaPhotos.length > 0 && (
          <>
            <h3>मीडिया कवरेज फ़ोटो</h3>
            <div className="event-thumbs row">
              {event.mediaPhotos.map((p, i) => (
                <img key={i} src={p} alt="media" height="80" />
              ))}
            </div>
          </>
        )}

        {event.video && (
          <>
            <h3>वीडियो</h3>
            <video controls width="400">
              <source src={event.video} type="video/mp4" />
            </video>
          </>
        )}

        <div className="form-actions">
          <Link to={`/update/${event.id}`} className="button">कार्यक्रम संपादित करें</Link>
          <Link to="/" className="button">वापस जाएँ</Link>
        </div>
      </main>
    </div>
  );
}
