import React, { useState, useEffect } from 'react';
import { listEvents } from '../api';

export default function Home({ onOpen, user, onAdd, onEdit, onReport, onLogout }) {
  const [type, setType] = useState('ongoing');
  const [events, setEvents] = useState([]);

  useEffect(() => {
    fetchEvents();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [type]);

  async function fetchEvents() {
    try {
      const r = await listEvents(type);
      setEvents(Array.isArray(r) ? r : []);
    } catch (err) {
      console.error('Error loading events:', err);
      setEvents([]);
    }
  }

  // 🩵 Prevent crash if user is missing
  if (!user) {
    return (
      <div style={{ padding: 20, background: '#fff', borderRadius: 8 }}>
        <h3>कृपया पहले लॉगिन करें</h3>
        <p>आपको इस पेज तक पहुंचने के लिए लॉगिन करना आवश्यक है।</p>
      </div>
    );
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
          <label>
            <input
              type='radio'
              checked={type === 'ongoing'}
              onChange={() => setType('ongoing')}
            />{' '}
            चल रही घटनाएँ
          </label>
          <label style={{ marginLeft: 12 }}>
            <input
              type='radio'
              checked={type === 'previous'}
              onChange={() => setType('previous')}
            />{' '}
            पिछले घटनाएँ
          </label>
        </div>
        <div>
          {user.role === 'admin' && (
            <button className='button' onClick={onAdd}>
              इवेंट जोड़ें
            </button>
          )}
          <button className='button secondary' onClick={onLogout}>
            लॉग आउट
          </button>
        </div>
      </div>

      <table className='table' style={{ marginTop: 12 }}>
        <thead>
          <tr>
            <th>S. No.</th>
            <th>Event Details (नाम)</th>
            <th>Event Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {events.length > 0 ? (
            events.map((e, i) => (
              <tr key={e.id || i}>
                <td>{i + 1}</td>
                <td>
                  <a
                    href='#'
                    onClick={(ev) => {
                      ev.preventDefault();
                      onOpen(e.id);
                    }}
                  >
                    {e.name}
                  </a>
                </td>
                <td>
                  {e.start_datetime} - {e.end_datetime}
                </td>
                <td>
                  <button className='button' onClick={() => onOpen(e.id)}>
                    डिटेल्स दिखाएँ
                  </button>
                  <button className='button secondary' onClick={() => onEdit(e.id)}>
                    अपडेट
                  </button>
                  {user.role === 'admin' && (
                    <button className='button' onClick={() => onReport(e.id)}>
                      रिपोर्ट
                    </button>
                  )}
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan='4' style={{ textAlign: 'center', padding: 20 }}>
                कोई इवेंट नहीं मिला।
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
