// src/api.js
import axios from "axios";

export const BASE = "http://localhost:4000/api";

export const addEvent = (data) => axios.post(`${BASE}/events`, data, {
  headers: { "Content-Type": "multipart/form-data" },
});

export const updateEvent = (id, data) => axios.post(`${BASE}/events/${id}/update`, data, {
  headers: { "Content-Type": "multipart/form-data" },
});

export const listEvents = (type) => axios.get(`${BASE}/events?type=${type}`);
export const getEvent = (id) => axios.get(`${BASE}/events/${id}`);
export const markViewed = (id, userId) => axios.post(`${BASE}/events/${id}/view`, { userId });
export const getReport = (id) => axios.get(`${BASE}/events/${id}/report`);
export const acceptReport = (id, userId) => axios.post(`${BASE}/events/${id}/report/accept`, { userId });
export const login = (code) => axios.post(`${BASE}/login`, { code });
